/* CSS Custom Properties for consistent theming */
:root {
    --primary-color: #0066cc;
    --primary-dark: #004499;
    --secondary-color: #6366f1;
    --success-color: #22c55e;
    --warning-color: #f59e0b;
    --danger-color: #ef4444;
    --info-color: #06b6d4;

    --bg-primary: #0f0f23;
    --bg-secondary: #1a1a35;
    --bg-tertiary: #252547;
    --bg-card: #2a2a4a;

    --text-primary: #ffffff;
    --text-secondary: #e2e8f0;
    --text-muted: #94a3b8;
    --text-accent: #60a5fa;

    --border-color: #374151;
    --border-accent: #4f46e5;

    --shadow-sm: 0 1px 2px 0 rgba(0, 0, 0, 0.05);
    --shadow-md: 0 4px 6px -1px rgba(0, 0, 0, 0.1);
    --shadow-lg: 0 10px 15px -3px rgba(0, 0, 0, 0.1);
    --shadow-xl: 0 20px 25px -5px rgba(0, 0, 0, 0.1);

    --border-radius: 8px;
    --border-radius-lg: 12px;
    --spacing-unit: 8px;
}

/* Base Styles */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
    background: linear-gradient(135deg, var(--bg-primary) 0%, var(--bg-secondary) 100%);
    color: var(--text-primary);
    line-height: 1.6;
    min-height: 100vh;
}

.container {
    max-width: 1400px;
    margin: 0 auto;
    padding: 0 calc(var(--spacing-unit) * 3);
}

/* Typography */
h1, h2, h3, h4, h5, h6 {
    font-weight: 600;
    line-height: 1.2;
    color: var(--text-primary);
}

h1 { font-size: 2.5rem; }
h2 { font-size: 1.875rem; }
h3 { font-size: 1.5rem; }

/* Header */
.header {
    background: var(--bg-card);
    border-bottom: 1px solid var(--border-color);
    padding: calc(var(--spacing-unit) * 3) 0;
    box-shadow: var(--shadow-md);
}

.header-content {
    display: flex;
    justify-content: space-between;
    align-items: center;
    gap: calc(var(--spacing-unit) * 4);
}

.logo h1 {
    background: linear-gradient(135deg, var(--primary-color), var(--secondary-color));
    -webkit-background-clip: text;
    background-clip: text;
    -webkit-text-fill-color: transparent;
    margin-bottom: calc(var(--spacing-unit) / 2);
}

.tagline {
    color: var(--text-muted);
    font-size: 0.875rem;
    font-weight: 400;
}

.header-stats {
    display: flex;
    gap: calc(var(--spacing-unit) * 4);
}

.stat {
    display: flex;
    flex-direction: column;
    align-items: center;
    text-align: center;
}

.stat-value {
    font-size: 1.5rem;
    font-weight: 700;
    color: var(--text-primary);
    margin-bottom: calc(var(--spacing-unit) / 2);
}

.stat-label {
    font-size: 0.75rem;
    color: var(--text-muted);
    text-transform: uppercase;
    letter-spacing: 0.05em;
}

/* Main Content */
.main {
    padding: calc(var(--spacing-unit) * 6) 0;
}

/* Dashboard Grid */
.dashboard-overview {
    margin-bottom: calc(var(--spacing-unit) * 8);
}

.dashboard-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: calc(var(--spacing-unit) * 3);
}

/* Cards */
.card {
    background: var(--bg-card);
    border: 1px solid var(--border-color);
    border-radius: var(--border-radius-lg);
    padding: calc(var(--spacing-unit) * 3);
    box-shadow: var(--shadow-lg);
    transition: all 0.3s ease;
}

.card:hover {
    transform: translateY(-2px);
    box-shadow: var(--shadow-xl);
    border-color: var(--border-accent);
}

.card-primary {
    border-color: var(--primary-color);
    background: linear-gradient(135deg, var(--bg-card) 0%, rgba(0, 102, 204, 0.05) 100%);
}

.card-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: calc(var(--spacing-unit) * 2);
    padding-bottom: calc(var(--spacing-unit) * 2);
    border-bottom: 1px solid var(--border-color);
}

.card-header h2 {
    font-size: 1.25rem;
    color: var(--text-primary);
}

.card-content {
    display: flex;
    flex-direction: column;
    gap: calc(var(--spacing-unit) * 2);
}

/* Status Indicators */
.status-indicator {
    width: 12px;
    height: 12px;
    border-radius: 50%;
    display: inline-block;
}

.status-success { 
    background: var(--success-color); 
    color: var(--success-color);
}
.status-warning { 
    background: var(--warning-color); 
    color: var(--warning-color);
}
.status-danger { 
    background: var(--danger-color); 
    color: var(--danger-color);
}
.status-active { 
    color: var(--success-color); 
}

/* Metrics */
.metric {
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.metric-value {
    font-size: 1.125rem;
    font-weight: 600;
    color: var(--text-accent);
}

.metric-label {
    font-size: 0.875rem;
    color: var(--text-muted);
}

/* Compliance */
.compliance-item {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: calc(var(--spacing-unit) * 1) 0;
}

.compliance-badge {
    background: var(--bg-tertiary);
    color: var(--text-secondary);
    padding: calc(var(--spacing-unit) / 2) calc(var(--spacing-unit) * 1);
    border-radius: calc(var(--border-radius) / 2);
    font-size: 0.75rem;
    font-weight: 500;
    text-transform: uppercase;
    letter-spacing: 0.05em;
}

/* Risk Management */
.risk-level {
    display: flex;
    align-items: center;
    gap: calc(var(--spacing-unit) * 1);
    margin-bottom: calc(var(--spacing-unit) * 2);
}

.risk-indicator {
    width: 16px;
    height: 16px;
    border-radius: 50%;
}

.risk-medium { background: var(--warning-color); }
.risk-high { background: var(--danger-color); }
.risk-low { background: var(--success-color); }

.risk-factors {
    display: flex;
    flex-direction: column;
    gap: calc(var(--spacing-unit) / 2);
    margin-bottom: calc(var(--spacing-unit) * 2);
}

.risk-factor {
    font-size: 0.875rem;
    color: var(--text-muted);
    padding-left: calc(var(--spacing-unit) * 2);
    position: relative;
}

.risk-factor::before {
    content: "•";
    position: absolute;
    left: 0;
    color: var(--warning-color);
}

.mitigation-active {
    display: flex;
    align-items: center;
    gap: calc(var(--spacing-unit) * 1);
    font-size: 0.875rem;
    color: var(--success-color);
}

/* Activity */
.activity-item {
    display: flex;
    gap: calc(var(--spacing-unit) * 2);
    padding: calc(var(--spacing-unit) * 1) 0;
    border-bottom: 1px solid rgba(255, 255, 255, 0.05);
}

.activity-item:last-child {
    border-bottom: none;
}

.activity-time {
    font-size: 0.75rem;
    color: var(--text-muted);
    font-weight: 500;
    min-width: 40px;
}

.activity-desc {
    font-size: 0.875rem;
    color: var(--text-secondary);
}

/* Rule Browser */
.rule-browser {
    margin-bottom: calc(var(--spacing-unit) * 8);
}

.section-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: calc(var(--spacing-unit) * 4);
}

.section-header h2 {
    color: var(--text-primary);
}

.filters {
    display: flex;
    gap: calc(var(--spacing-unit) * 2);
}

.filter-select {
    background: var(--bg-tertiary);
    border: 1px solid var(--border-color);
    border-radius: var(--border-radius);
    color: var(--text-secondary);
    padding: calc(var(--spacing-unit) * 1) calc(var(--spacing-unit) * 2);
    font-size: 0.875rem;
    cursor: pointer;
    transition: all 0.2s ease;
}

.filter-select:hover {
    border-color: var(--border-accent);
}

.filter-select:focus {
    outline: none;
    border-color: var(--primary-color);
    box-shadow: 0 0 0 2px rgba(0, 102, 204, 0.2);
}

.rules-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
    gap: calc(var(--spacing-unit) * 3);
}

.rule-card {
    background: var(--bg-card);
    border: 1px solid var(--border-color);
    border-radius: var(--border-radius-lg);
    padding: calc(var(--spacing-unit) * 3);
    transition: all 0.3s ease;
    cursor: pointer;
}

.rule-card:hover {
    transform: translateY(-2px);
    box-shadow: var(--shadow-lg);
    border-color: var(--border-accent);
}

.rule-header {
    display: flex;
    justify-content: space-between;
    align-items: flex-start;
    margin-bottom: calc(var(--spacing-unit) * 2);
}

.rule-id {
    font-size: 0.75rem;
    color: var(--text-muted);
    font-weight: 500;
    text-transform: uppercase;
    letter-spacing: 0.05em;
}

.rule-title {
    font-size: 1.125rem;
    font-weight: 600;
    color: var(--text-primary);
    margin: calc(var(--spacing-unit) / 2) 0;
}

.rule-category {
    display: inline-block;
    background: var(--secondary-color);
    color: white;
    padding: calc(var(--spacing-unit) / 2) calc(var(--spacing-unit) * 1);
    border-radius: calc(var(--border-radius) / 2);
    font-size: 0.75rem;
    font-weight: 500;
    text-transform: capitalize;
}

.rule-category.prime { background: var(--danger-color); }
.rule-category.process { background: var(--info-color); }
.rule-category.quality-gates { background: var(--warning-color); }
.rule-category.agents { background: var(--secondary-color); }
.rule-category.env { background: var(--success-color); }
.rule-category.success { background: var(--primary-color); }

/* Architecture Diagram */
.architecture {
    margin-bottom: calc(var(--spacing-unit) * 8);
}

.architecture-diagram {
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    align-items: center;
    gap: calc(var(--spacing-unit) * 3);
    padding: calc(var(--spacing-unit) * 6);
    background: var(--bg-card);
    border-radius: var(--border-radius-lg);
    border: 1px solid var(--border-color);
}

.arch-component {
    background: var(--bg-tertiary);
    border: 2px solid var(--border-accent);
    border-radius: var(--border-radius-lg);
    padding: calc(var(--spacing-unit) * 3);
    text-align: center;
    min-width: 150px;
    transition: all 0.3s ease;
}

.arch-component:hover {
    transform: scale(1.05);
    box-shadow: var(--shadow-lg);
}

.arch-component h3 {
    color: var(--text-primary);
    margin-bottom: calc(var(--spacing-unit) * 1);
}

.arch-component p {
    color: var(--text-muted);
    font-size: 0.875rem;
}

.arch-arrow {
    font-size: 1.5rem;
    color: var(--text-accent);
    font-weight: bold;
}

.arch-down {
    width: 100%;
    text-align: center;
}

/* Footer */
.footer {
    background: var(--bg-secondary);
    border-top: 1px solid var(--border-color);
    padding: calc(var(--spacing-unit) * 4) 0;
    margin-top: auto;
}

.footer-content {
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.footer-info {
    display: flex;
    flex-direction: column;
    gap: calc(var(--spacing-unit) / 2);
}

.footer-info span {
    color: var(--text-muted);
    font-size: 0.875rem;
}

.footer-links {
    display: flex;
    gap: calc(var(--spacing-unit) * 3);
}

.footer-link {
    color: var(--text-muted);
    text-decoration: none;
    font-size: 0.875rem;
    transition: color 0.2s ease;
}

.footer-link:hover {
    color: var(--text-accent);
}

/* Responsive Design */
@media (max-width: 768px) {
    .container {
        padding: 0 calc(var(--spacing-unit) * 2);
    }

    .header-content {
        flex-direction: column;
        gap: calc(var(--spacing-unit) * 2);
    }

    .header-stats {
        gap: calc(var(--spacing-unit) * 2);
    }

    .dashboard-grid {
        grid-template-columns: 1fr;
    }

    .section-header {
        flex-direction: column;
        gap: calc(var(--spacing-unit) * 2);
        align-items: stretch;
    }

    .filters {
        justify-content: stretch;
    }

    .filter-select {
        flex: 1;
    }

    .rules-grid {
        grid-template-columns: 1fr;
    }

    .architecture-diagram {
        flex-direction: column;
    }

    .arch-arrow {
        transform: rotate(90deg);
    }

    .arch-down {
        transform: none;
    }

    .footer-content {
        flex-direction: column;
        gap: calc(var(--spacing-unit) * 2);
        text-align: center;
    }
}

/* Animations */
@keyframes fadeIn {
    from { opacity: 0; transform: translateY(20px); }
    to { opacity: 1; transform: translateY(0); }
}

.card, .rule-card {
    animation: fadeIn 0.6s ease-out;
}

/* Loading States */
.loading {
    opacity: 0.6;
    pointer-events: none;
}

.loading::after {
    content: "";
    position: absolute;
    top: 50%;
    left: 50%;
    width: 20px;
    height: 20px;
    margin: -10px 0 0 -10px;
    border: 2px solid var(--primary-color);
    border-top-color: transparent;
    border-radius: 50%;
    animation: spin 1s linear infinite;
}

@keyframes spin {
    to { transform: rotate(360deg); }
}

/* Dark mode enhancements */
::selection {
    background: rgba(0, 102, 204, 0.3);
    color: var(--text-primary);
}

::-webkit-scrollbar {
    width: 8px;
}

::-webkit-scrollbar-track {
    background: var(--bg-secondary);
}

::-webkit-scrollbar-thumb {
    background: var(--border-color);
    border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
    background: var(--border-accent);
}

