/**
 * CLAUDE CODEX Dashboard
 * Interactive JavaScript for rule management interface
 */

class CodexDashboard {
    constructor() {
        this.rules = [];
        this.filteredRules = [];
        this.init();
    }

    async init() {
        try {
            await this.loadData();
            this.setupEventListeners();
            this.renderRules();
            this.updateStats();
            this.startPerformanceMonitoring();
        } catch (error) {
            console.error('Dashboard initialization failed:', error);
            this.showError('Failed to load configuration data');
        }
    }

    async loadData() {
        try {
            const response = await fetch('data/INDEX.json');
            if (!response.ok) {
                throw new Error(`HTTP ${response.status}: ${response.statusText}`);
            }

            const data = await response.json();
            this.rules = data.items || [];
            this.filteredRules = [...this.rules];

            console.log(`Loaded ${this.rules.length} rules from INDEX.json`);
        } catch (error) {
            console.error('Failed to load rule data:', error);
            // Fallback to mock data for demo purposes
            this.loadMockData();
        }
    }

    loadMockData() {
        console.log('Loading mock data for demonstration');
        this.rules = [
            { id: 'R-prime-0001', title: 'Authority Delegation', type: 'prime', status: 'active', priority: 'critical' },
            { id: 'R-prime-0002', title: 'Single Source of Truth', type: 'prime', status: 'active', priority: 'critical' },
            { id: 'R-process-0001', title: 'Complete Process Execution', type: 'process', status: 'active', priority: 'high' },
            { id: 'R-quality-gates-0001', title: 'Pre-Execution Checklist', type: 'quality-gates', status: 'active', priority: 'medium' },
            { id: 'R-agents-0001', title: 'Reactive Intelligence', type: 'agents', status: 'active', priority: 'high' }
        ];
        this.filteredRules = [...this.rules];
    }

    setupEventListeners() {
        // Filter controls
        const categoryFilter = document.getElementById('category-filter');
        const statusFilter = document.getElementById('status-filter');

        if (categoryFilter) {
            categoryFilter.addEventListener('change', () => this.applyFilters());
        }

        if (statusFilter) {
            statusFilter.addEventListener('change', () => this.applyFilters());
        }

        // Rule card clicks
        document.addEventListener('click', (event) => {
            const ruleCard = event.target.closest('.rule-card');
            if (ruleCard) {
                const ruleId = ruleCard.dataset.ruleId;
                this.showRuleDetails(ruleId);
            }
        });

        // Responsive handling
        window.addEventListener('resize', () => {
            this.handleResize();
        });
    }

    applyFilters() {
        const categoryFilter = document.getElementById('category-filter');
        const statusFilter = document.getElementById('status-filter');

        const selectedCategory = categoryFilter ? categoryFilter.value : '';
        const selectedStatus = statusFilter ? statusFilter.value : '';

        this.filteredRules = this.rules.filter(rule => {
            const categoryMatch = !selectedCategory || rule.type === selectedCategory;
            const statusMatch = !selectedStatus || (rule.status || 'active') === selectedStatus;

            return categoryMatch && statusMatch;
        });

        this.renderRules();

        console.log(`Filtered to ${this.filteredRules.length} rules (category: ${selectedCategory || 'all'}, status: ${selectedStatus || 'all'})`);
    }

    renderRules() {
        const rulesGrid = document.getElementById('rules-grid');
        if (!rulesGrid) return;

        if (this.filteredRules.length === 0) {
            rulesGrid.innerHTML = this.getEmptyStateHTML();
            return;
        }

        const rulesHTML = this.filteredRules
            .map(rule => this.createRuleCardHTML(rule))
            .join('');

        rulesGrid.innerHTML = rulesHTML;

        // Add stagger animation
        setTimeout(() => {
            const cards = rulesGrid.querySelectorAll('.rule-card');
            cards.forEach((card, index) => {
                card.style.animationDelay = `${index * 100}ms`;
                card.classList.add('fade-in');
            });
        }, 50);
    }

    createRuleCardHTML(rule) {
        const status = rule.status || 'active';
        const priority = rule.priority || 'medium';
        const categoryClass = this.getCategoryClass(rule.type);
        const priorityIndicator = this.getPriorityIndicator(priority);

        return `
            <div class="rule-card" data-rule-id="${rule.id}" data-category="${rule.type}" data-status="${status}">
                <div class="rule-header">
                    <div>
                        <div class="rule-id">${rule.id}</div>
                        <div class="rule-title">${rule.title}</div>
                    </div>
                    <div class="rule-meta">
                        <span class="rule-category ${categoryClass}">${rule.type}</span>
                        ${priorityIndicator}
                    </div>
                </div>
                <div class="rule-content">
                    <div class="rule-stats">
                        <span class="stat-item">
                            <span class="status-indicator status-${status}"></span>
                            ${this.capitalize(status)}
                        </span>
                        <span class="stat-item">
                            Priority: ${this.capitalize(priority)}
                        </span>
                    </div>
                    <div class="rule-actions">
                        <button class="btn btn-outline" onclick="event.stopPropagation(); window.open('${rule.path || '#'}', '_blank')">
                            View Source
                        </button>
                    </div>
                </div>
            </div>
        `;
    }

    getCategoryClass(category) {
        const classMap = {
            'prime': 'prime',
            'process': 'process',
            'quality-gates': 'quality-gates',
            'agents': 'agents',
            'env': 'env',
            'success': 'success'
        };
        return classMap[category] || 'default';
    }

    getPriorityIndicator(priority) {
        const indicators = {
            'critical': '<span class="priority-indicator critical">🔴</span>',
            'high': '<span class="priority-indicator high">🟡</span>',
            'medium': '<span class="priority-indicator medium">🟢</span>',
            'low': '<span class="priority-indicator low">⚪</span>'
        };
        return indicators[priority] || indicators['medium'];
    }

    getEmptyStateHTML() {
        return `
            <div class="empty-state">
                <div class="empty-state-icon">📋</div>
                <h3>No rules match your filters</h3>
                <p>Try adjusting your filter criteria to see more results.</p>
                <button class="btn btn-primary" onclick="dashboard.clearFilters()">
                    Clear Filters
                </button>
            </div>
        `;
    }

    clearFilters() {
        const categoryFilter = document.getElementById('category-filter');
        const statusFilter = document.getElementById('status-filter');

        if (categoryFilter) categoryFilter.value = '';
        if (statusFilter) statusFilter.value = '';

        this.applyFilters();
    }

    showRuleDetails(ruleId) {
        const rule = this.rules.find(r => r.id === ruleId);
        if (!rule) return;

        // Create modal or navigate to detailed view
        console.log('Showing details for rule:', rule);

        // For now, just highlight the selected rule
        document.querySelectorAll('.rule-card').forEach(card => {
            card.classList.remove('selected');
        });

        const selectedCard = document.querySelector(`[data-rule-id="${ruleId}"]`);
        if (selectedCard) {
            selectedCard.classList.add('selected');
            selectedCard.scrollIntoView({ behavior: 'smooth', block: 'center' });
        }
    }

    updateStats() {
        // Update header stats
        const totalRulesElement = document.getElementById('total-rules');
        if (totalRulesElement) {
            totalRulesElement.textContent = this.rules.length;
        }

        // Calculate and display additional metrics
        const activeRules = this.rules.filter(r => (r.status || 'active') === 'active').length;
        const criticalRules = this.rules.filter(r => (r.priority || 'medium') === 'critical').length;

        console.log(`Stats - Total: ${this.rules.length}, Active: ${activeRules}, Critical: ${criticalRules}`);
    }

    startPerformanceMonitoring() {
        // Simulate real-time performance metrics
        setInterval(() => {
            this.updatePerformanceMetrics();
        }, 5000);

        // Initial update
        this.updatePerformanceMetrics();
    }

    updatePerformanceMetrics() {
        // Simulate performance data
        const metrics = {
            validationTime: Math.floor(Math.random() * 200) + 300, // 300-500ms
            linkResolution: Math.floor(Math.random() * 50) + 50,   // 50-100ms
            indexRebuild: Math.floor(Math.random() * 1000) + 1000  // 1-2s
        };

        // Update metric displays if they exist
        const metricElements = {
            'rule-validation-time': `< ${metrics.validationTime}ms`,
            'link-resolution-time': `< ${metrics.linkResolution}ms`,
            'index-rebuild-time': `< ${Math.floor(metrics.indexRebuild/1000)}s`
        };

        Object.entries(metricElements).forEach(([id, value]) => {
            const element = document.getElementById(id);
            if (element) {
                element.textContent = value;
            }
        });

        // Add subtle indicator of live updates
        const indicators = document.querySelectorAll('.status-indicator.status-success');
        indicators.forEach(indicator => {
            indicator.style.animation = 'pulse 0.5s ease-in-out';
            setTimeout(() => {
                indicator.style.animation = '';
            }, 500);
        });
    }

    handleResize() {
        // Handle responsive layout adjustments
        const dashboard = document.querySelector('.dashboard-grid');
        if (dashboard && window.innerWidth < 768) {
            dashboard.classList.add('mobile-layout');
        } else if (dashboard) {
            dashboard.classList.remove('mobile-layout');
        }
    }

    showError(message) {
        const errorHTML = `
            <div class="error-banner">
                <span class="error-icon">⚠️</span>
                <span class="error-message">${message}</span>
                <button class="error-dismiss" onclick="this.parentElement.remove()">×</button>
            </div>
        `;

        document.body.insertAdjacentHTML('afterbegin', errorHTML);

        // Auto-dismiss after 5 seconds
        setTimeout(() => {
            const banner = document.querySelector('.error-banner');
            if (banner) banner.remove();
        }, 5000);
    }

    capitalize(str) {
        return str.charAt(0).toUpperCase() + str.slice(1);
    }
}

// Additional CSS for dynamic elements
const additionalStyles = `
    .fade-in {
        animation: fadeIn 0.6s ease-out forwards;
    }

    .rule-card.selected {
        border-color: var(--primary-color);
        box-shadow: 0 0 20px rgba(0, 102, 204, 0.3);
        transform: scale(1.02);
    }

    .rule-content {
        margin-top: calc(var(--spacing-unit) * 2);
    }

    .rule-stats {
        display: flex;
        justify-content: space-between;
        margin-bottom: calc(var(--spacing-unit) * 2);
        font-size: 0.875rem;
    }

    .stat-item {
        display: flex;
        align-items: center;
        gap: calc(var(--spacing-unit) / 2);
        color: var(--text-muted);
    }

    .rule-actions {
        display: flex;
        gap: calc(var(--spacing-unit) * 1);
    }

    .btn {
        padding: calc(var(--spacing-unit) * 1) calc(var(--spacing-unit) * 2);
        border-radius: var(--border-radius);
        font-size: 0.875rem;
        font-weight: 500;
        cursor: pointer;
        transition: all 0.2s ease;
        text-decoration: none;
        display: inline-flex;
        align-items: center;
        justify-content: center;
        border: 1px solid transparent;
    }

    .btn-primary {
        background: var(--primary-color);
        color: white;
    }

    .btn-primary:hover {
        background: var(--primary-dark);
        transform: translateY(-1px);
    }

    .btn-outline {
        background: transparent;
        color: var(--text-accent);
        border-color: var(--border-accent);
    }

    .btn-outline:hover {
        background: var(--border-accent);
        color: white;
        transform: translateY(-1px);
    }

    .priority-indicator {
        font-size: 0.75rem;
