        ...currentRule.metadata,
        lastEditor: args.editorId,
        modified: Date.now()
      }
    });
  },
});
```

#### 3. Version Control & Audit Trail
```typescript
// Comprehensive rule versioning system
export const getRuleHistory = query({
  args: { ruleId: v.id("rules") },
  handler: async (ctx, args) => {
    const history = await ctx.db
      .query("ruleHistory")
      .filter(q => q.eq(q.field("originalRuleId"), args.ruleId))
      .order("desc")
      .collect();

    return history.map(entry => ({
      ...entry,
      diff: calculateDiff(entry.previousContent, entry.newContent),
      authorInfo: getUserInfo(entry.author)
    }));
