{
  "permissions": {
    "allow": [
      "Bash(npm run lint)",
      "Bash(npm run test)",
      "Bash(npm run build)",
      "Bash(node .claude/tools/*)",
      "Bash(git add:*)",
      "Bash(git commit:*)",
      "Bash(git status)",
      "Bash(git diff)",
      "Bash(ls:*)",
      "Bash(head:*)",
      "Bash(tail:*)",
      "Bash(grep:*)",
      "Bash(find:*)"
    ],
    "deny": [
      "Bash(rm -rf)",
      "Bash(curl:*)",
      "Bash(wget:*)",
      "Bash(sudo:*)",
      "Bash(chmod 777:*)"
    ]
  },
  "env": {
    "CLAUDE_AUTO_REVIEW": "true",
    "CLAUDE_DEBUG": "false",
    "ENTERPRISE_STANDARDS": "enabled",
    "VALIDATION_LEVEL": "strict"
  },
  "model": "claude-3-5-sonnet-20241022",
  "agents": {
    "review-orchestrator": {
      "description": "Orchestrates complex code reviews with automation",
      "model": "claude-3-5-sonnet-20241022"
    },
    "auto-fix-specialist": {
      "description": "Implements automated code improvements safely",
      "model": "claude-3-5-sonnet-20241022"
    },
    "template-processor": {
      "description": "Processes templates with variable substitution",
      "model": "claude-3-5-sonnet-20241022"
    }
  },
  "hooks": {
    "enabled": true,
    "sessionStart": true,
    "preToolUse": true,
    "postToolUse": true
  }
}

