#!/usr/bin/env node

/**
 * Auto-Fix Engine
 * Implements automated fixes for common code improvements
 */

const fs = require("fs");
const path = require("path");
const { execSync } = require("child_process");

class AutoFixEngine {
  constructor(options = {}) {
    this.options = {
      dryRun: false,
      backup: true,
      verbose: false,
      ...options,
    };

    this.fixers = {
      dependency: new DependencyFixer(this.options),
      errorHandling: new ErrorHandlingFixer(this.options),
      codeQuality: new CodeQualityFixer(this.options),
      security: new SecurityFixer(this.options),
      logging: new LoggingFixer(this.options),
      performance: new PerformanceFixer(this.options),
    };

    this.results = {
      applied: [],
      skipped: [],
      errors: [],
      warnings: [],
    };
  }

  /**
   * Apply auto-fixes based on categorized issues
   * @param {Array} issues - Array of auto-fixable issues
   * @param {Object} options - Fix options
   * @returns {Object} Fix results
   */
  async applyFixes(issues, options = {}) {
    this.log("Starting auto-fix process...", "info");

    // Group issues by file for efficient processing
    const issuesByFile = this.groupIssuesByFile(issues);

    for (const [filePath, fileIssues] of Object.entries(issuesByFile)) {
      try {
        await this.processFile(filePath, fileIssues, options);
      } catch (error) {
        this.results.errors.push({
          file: filePath,
          error: error.message,
        });
      }
    }

    return this.generateReport();
  }

  /**
   * Process fixes for a single file
   * @param {string} filePath - File path
   * @param {Array} issues - Issues for this file
   * @param {Object} options - Processing options
   */
  async processFile(filePath, issues, options) {
    if (!fs.existsSync(filePath)) {
      this.results.skipped.push({
        file: filePath,
        reason: "File not found",
      });
      return;
    }

    // Create backup if requested
    if (this.options.backup) {
      this.createBackup(filePath);
    }

    let content = fs.readFileSync(filePath, "utf8");
    const originalContent = content;
    let modificationsCount = 0;

    this.log(`Processing ${filePath}...`, "info");

    // Apply fixes in order of safety
    for (const issue of this.prioritizeIssues(issues)) {
      try {
        const fixer = this.selectFixer(issue);
        if (fixer) {
          const result = await fixer.fix(content, issue, filePath);
          if (result.modified) {
            content = result.content;
            modificationsCount++;
            this.results.applied.push({
              file: filePath,
              issue: issue.title,
              fix: result.description,
            });
            this.log(`  ‚úì Fixed: ${issue.title}`, "success");
          } else {
            this.results.skipped.push({
              file: filePath,
              issue: issue.title,
              reason: result.reason || "No changes needed",
            });
          }
        }
      } catch (error) {
        this.results.errors.push({
          file: filePath,
          issue: issue.title,
          error: error.message,
        });
        this.log(`  ‚úó Error fixing ${issue.title}: ${error.message}`, "error");
      }
    }

    // Write modified content
    if (modificationsCount > 0 && !this.options.dryRun) {
      fs.writeFileSync(filePath, content, "utf8");
      this.log(
        `  üíæ Saved ${modificationsCount} fixes to ${filePath}`,
        "success"
      );

      // Run formatter if available
      await this.runFormatter(filePath);
    }
  }

  /**
   * Select appropriate fixer for an issue
   * @param {Object} issue - Issue object
   * @returns {Object} Fixer instance
   */
  selectFixer(issue) {
    const text = `${issue.title} ${issue.description}`.toLowerCase();

    if (
      text.includes("dependency") ||
      text.includes("package") ||
      text.includes("npm")
    ) {
      return this.fixers.dependency;
    }
    if (
      text.includes("error") ||
      text.includes("exception") ||
      text.includes("try") ||
      text.includes("catch")
    ) {
      return this.fixers.errorHandling;
    }
    if (
      text.includes("security") ||
      text.includes("vulnerability") ||
      text.includes("injection")
    ) {
      return this.fixers.security;
    }
    if (
      text.includes("logging") ||
      text.includes("console") ||
      text.includes("debug")
    ) {
      return this.fixers.logging;
    }
    if (
      text.includes("performance") ||
      text.includes("slow") ||
      text.includes("memory")
    ) {
      return this.fixers.performance;
    }
    if (
      text.includes("import") ||
      text.includes("type") ||
      text.includes("format") ||
      text.includes("lint")
    ) {
      return this.fixers.codeQuality;
    }

    return null;
  }

  /**
   * Group issues by file path
   * @param {Array} issues - Array of issues
   * @returns {Object} Issues grouped by file
   */
  groupIssuesByFile(issues) {
    const grouped = {};
    for (const issue of issues) {
      const file = issue.file || "unknown";
      if (!grouped[file]) grouped[file] = [];
      grouped[file].push(issue);
    }
    return grouped;
  }

  /**
   * Prioritize issues for safe application
   * @param {Array} issues - Array of issues
   * @returns {Array} Prioritized issues
   */
  prioritizeIssues(issues) {
    const priorities = {
      import: 1,
      format: 2,
      type: 3,
      error: 4,
      dependency: 5,
      security: 6,
    };

    return issues.sort((a, b) => {
      const aText = `${a.title} ${a.description}`.toLowerCase();
      const bText = `${b.title} ${b.description}`.toLowerCase();

      const aPriority =
        Object.entries(priorities).find(([key]) => aText.includes(key))?.[1] ||
        10;
      const bPriority =
        Object.entries(priorities).find(([key]) => bText.includes(key))?.[1] ||
        10;

      return aPriority - bPriority;
    });
  }

  /**
   * Create backup of file
   * @param {string} filePath - File to backup
   */
  createBackup(filePath) {
    const backupPath = `${filePath}.backup.${Date.now()}`;
    fs.copyFileSync(filePath, backupPath);
    this.log(`Created backup: ${backupPath}`, "info");
  }

  /**
   * Run code formatter on file
   * @param {string} filePath - File to format
   */
  async runFormatter(filePath) {
    try {
      const ext = path.extname(filePath);

      if ([".js", ".ts", ".jsx", ".tsx"].includes(ext)) {
        // Try Prettier first
        try {
          execSync(`npx prettier --write "${filePath}"`, { stdio: "pipe" });
          this.log(`  üé® Formatted with Prettier: ${filePath}`, "info");
        } catch {
          // Fall back to ESLint
          try {
            execSync(`npx eslint --fix "${filePath}"`, { stdio: "pipe" });
            this.log(`  üé® Formatted with ESLint: ${filePath}`, "info");
          } catch {
            // Formatter not available
          }
        }
      } else if (ext === ".py") {
        try {
          execSync(`black "${filePath}"`, { stdio: "pipe" });
          this.log(`  üé® Formatted with Black: ${filePath}`, "info");
        } catch {
          // Black not available
        }
      }
    } catch (error) {
      this.log(
        `Warning: Could not format ${filePath}: ${error.message}`,
        "warning"
      );
    }
  }

  /**
   * Generate fix report
   * @returns {Object} Fix report
   */
  generateReport() {
    const totalIssues =
      this.results.applied.length +
      this.results.skipped.length +
      this.results.errors.length;

    return {
      summary: {
        totalIssues,
        applied: this.results.applied.length,
        skipped: this.results.skipped.length,
        errors: this.results.errors.length,
        successRate:
          totalIssues > 0
            ? ((this.results.applied.length / totalIssues) * 100).toFixed(1)
            : 0,
      },
      details: {
        applied: this.results.applied,
        skipped: this.results.skipped,
        errors: this.results.errors,
        warnings: this.results.warnings,
      },
      timestamp: new Date().toISOString(),
    };
  }

  /**
   * Log message with level
   * @param {string} message - Message to log
   * @param {string} level - Log level
   */
  log(message, level = "info") {
    if (!this.options.verbose && level === "info") return;

    const colors = {
      info: "\x1b[36m", // Cyan
      success: "\x1b[32m", // Green
      warning: "\x1b[33m", // Yellow
      error: "\x1b[31m", // Red
      reset: "\x1b[0m", // Reset
    };

    const color = colors[level] || colors.info;
    console.log(`${color}${message}${colors.reset}`);
  }
}

/**
 * Dependency Fixer
 * Handles package and dependency-related issues
 */
class DependencyFixer {
  constructor(options) {
    this.options = options;
  }

  async fix(content, issue, filePath) {
    const text = `${issue.title} ${issue.description}`.toLowerCase();

    // Update package.json dependencies
    if (filePath.endsWith("package.json") && text.includes("update")) {
      return this.updatePackageJson(content, issue);
    }

    // Add missing imports
    if (text.includes("missing") && text.includes("import")) {
      return this.addMissingImport(content, issue, filePath);
    }

    // Fix import paths
    if (text.includes("import") && text.includes("path")) {
      return this.fixImportPath(content, issue);
    }

    return { modified: false, reason: "No applicable dependency fix" };
  }

  updatePackageJson(content, issue) {
    try {
      const pkg = JSON.parse(content);
      let modified = false;

      // Extract package name and version from issue description
      const match = issue.description.match(/(\S+)@(\S+)/);
      if (match) {
        const [, packageName, version] = match;

        if (pkg.dependencies && pkg.dependencies[packageName]) {
          pkg.dependencies[packageName] = version;
          modified = true;
        } else if (pkg.devDependencies && pkg.devDependencies[packageName]) {
          pkg.devDependencies[packageName] = version;
          modified = true;
        }
      }

      if (modified) {
        return {
          modified: true,
          content: JSON.stringify(pkg, null, 2) + "\n",
          description: `Updated package dependency: ${match[1]}`,
        };
      }
    } catch (error) {
      throw new Error(`Failed to parse package.json: ${error.message}`);
    }

    return { modified: false, reason: "No dependency updates found" };
  }

  addMissingImport(content, issue, filePath) {
    const ext = path.extname(filePath);

    if ([".js", ".ts", ".jsx", ".tsx"].includes(ext)) {
      return this.addJavaScriptImport(content, issue);
    } else if (ext === ".py") {
      return this.addPythonImport(content, issue);
    }

    return { modified: false, reason: "Unsupported file type for import fix" };
  }

  addJavaScriptImport(content, issue) {
    // Simple heuristic to add common missing imports
    const missingImports = {
      react: "import React from 'react';",
      "react dom": "import ReactDOM from 'react-dom';",
      lodash: "import _ from 'lodash';",
      moment: "import moment from 'moment';",
      axios: "import axios from 'axios';",
    };

    const text = issue.description.toLowerCase();
    for (const [keyword, importStatement] of Object.entries(missingImports)) {
      if (text.includes(keyword)) {
        const lines = content.split("\n");
        const importIndex = lines.findIndex((line) =>
          line.startsWith("import")
        );

        if (importIndex >= 0 && !content.includes(importStatement)) {
          lines.splice(importIndex, 0, importStatement);
          return {
            modified: true,
            content: lines.join("\n"),
            description: `Added missing import: ${keyword}`,
          };
        }
      }
    }

    return { modified: false, reason: "No applicable import fix found" };
  }

  addPythonImport(content, issue) {
    // Simple heuristic for Python imports
    const missingImports = {
      json: "import json",
      os: "import os",
      sys: "import sys",
      datetime: "from datetime import datetime",
      requests: "import requests",
    };

    const text = issue.description.toLowerCase();
    for (const [keyword, importStatement] of Object.entries(missingImports)) {
      if (text.includes(keyword)) {
        const lines = content.split("\n");
        const importIndex = lines.findIndex(
          (line) => line.startsWith("import") || line.startsWith("from")
        );

        if (importIndex >= 0 && !content.includes(importStatement)) {
          lines.splice(importIndex, 0, importStatement);
          return {
            modified: true,
            content: lines.join("\n"),
            description: `Added missing import: ${keyword}`,
          };
        }
      }
    }

    return { modified: false, reason: "No applicable Python import fix found" };
  }

  fixImportPath(content, issue) {
    // Fix relative import paths
    const lines = content.split("\n");
    let modified = false;

    for (let i = 0; i < lines.length; i++) {
      const line = lines[i];
      if (line.includes("import") && line.includes("./")) {
        // Simple path normalization
        const fixed = line.replace(/\.\/+/g, "./");
        if (fixed !== line) {
          lines[i] = fixed;
          modified = true;
        }
      }
    }

    if (modified) {
      return {
        modified: true,
        content: lines.join("\n"),
        description: "Fixed import paths",
      };
    }

    return { modified: false, reason: "No import path fixes needed" };
  }
}

/**
 * Error Handling Fixer
 * Adds proper error handling and logging
 */
class ErrorHandlingFixer {
  constructor(options) {
    this.options = options;
  }

  async fix(content, issue, filePath) {
    const text = `${issue.title} ${issue.description}`.toLowerCase();
    const ext = path.extname(filePath);

    if (
      text.includes("try") ||
      text.includes("catch") ||
      text.includes("error")
    ) {
      if ([".js", ".ts", ".jsx", ".tsx"].includes(ext)) {
        return this.addJavaScriptErrorHandling(content, issue);
      } else if (ext === ".py") {
        return this.addPythonErrorHandling(content, issue);
      }
    }

    if (text.includes("log") || text.includes("logging")) {
      return this.addLogging(content, issue, filePath);
    }

    return { modified: false, reason: "No applicable error handling fix" };
  }

  addJavaScriptErrorHandling(content, issue) {
    const lines = content.split("\n");
    let modified = false;

    // Look for async functions without error handling
    for (let i = 0; i < lines.length; i++) {
      const line = lines[i].trim();

      if (
        line.includes("async function") ||
        (line.includes("const") && line.includes("async"))
      ) {
        // Check if the function body has try-catch
        const funcEnd = this.findFunctionEnd(lines, i);
        const funcBody = lines.slice(i, funcEnd).join("\n");

        if (!funcBody.includes("try") && !funcBody.includes("catch")) {
          // Add try-catch wrapper
          const indentation = this.getIndentation(lines[i + 1] || "");
          lines.splice(i + 1, 0, `${indentation}try {`);
          lines.splice(funcEnd + 1, 0, `${indentation}} catch (error) {`);
          lines.splice(
            funcEnd + 2,
            0,
            `${indentation}  console.error('Error:', error);`
          );
          lines.splice(funcEnd + 3, 0, `${indentation}  throw error;`);
          lines.splice(funcEnd + 4, 0, `${indentation}}`);
          modified = true;
          break;
        }
      }
    }

    if (modified) {
      return {
        modified: true,
        content: lines.join("\n"),
        description: "Added try-catch error handling",
      };
    }

    return { modified: false, reason: "No error handling improvements found" };
  }

  addPythonErrorHandling(content, issue) {
    const lines = content.split("\n");
    let modified = false;

    // Look for functions without error handling
    for (let i = 0; i < lines.length; i++) {
      const line = lines[i].trim();

      if (line.startsWith("def ")) {
        // Check if the function has try-except
        const funcEnd = this.findPythonFunctionEnd(lines, i);
        const funcBody = lines.slice(i, funcEnd).join("\n");

        if (!funcBody.includes("try:") && !funcBody.includes("except")) {
          // Add try-except wrapper
          const indentation = this.getIndentation(lines[i + 1] || "");
          lines.splice(i + 1, 0, `${indentation}try:`);
          lines.splice(funcEnd + 1, 0, `${indentation}except Exception as e:`);
          lines.splice(
            funcEnd + 2,
            0,
            `${indentation}    print(f"Error: {e}")`
          );
          lines.splice(funcEnd + 3, 0, `${indentation}    raise`);
          modified = true;
          break;
        }
      }
    }

    if (modified) {
      return {
        modified: true,
        content: lines.join("\n"),
        description: "Added try-except error handling",
      };
    }

    return {
      modified: false,
      reason: "No Python error handling improvements found",
    };
  }

  addLogging(content, issue, filePath) {
    const ext = path.extname(filePath);
    const lines = content.split("\n");
    let modified = false;

    if ([".js", ".ts", ".jsx", ".tsx"].includes(ext)) {
      // Add console.error for error cases
      for (let i = 0; i < lines.length; i++) {
        const line = lines[i];
        if (line.includes("throw ") && !line.includes("console.")) {
          const indentation = this.getIndentation(line);
          lines.splice(
            i,
            0,
            `${indentation}console.error('Error occurred:', error);`
          );
          modified = true;
          break;
        }
      }
    } else if (ext === ".py") {
      // Add logging import if not present
      if (!content.includes("import logging")) {
        lines.unshift("import logging");
        modified = true;
      }
    }

    if (modified) {
      return {
        modified: true,
        content: lines.join("\n"),
        description: "Added error logging",
      };
    }

    return { modified: false, reason: "No logging improvements needed" };
  }

  findFunctionEnd(lines, startIndex) {
    let braceCount = 0;
    for (let i = startIndex; i < lines.length; i++) {
      const line = lines[i];
      braceCount += (line.match(/{/g) || []).length;
      braceCount -= (line.match(/}/g) || []).length;
      if (braceCount === 0 && i > startIndex) {
        return i;
      }
    }
    return lines.length;
  }

  findPythonFunctionEnd(lines, startIndex) {
    const baseIndentation = this.getIndentationLevel(lines[startIndex]);
    for (let i = startIndex + 1; i < lines.length; i++) {
      const line = lines[i].trim();
      if (line && this.getIndentationLevel(lines[i]) <= baseIndentation) {
        return i - 1;
      }
    }
    return lines.length;
  }

  getIndentation(line) {
    const match = line.match(/^(\s*)/);
    return match ? match[1] : "";
  }

  getIndentationLevel(line) {
    return this.getIndentation(line).length;
  }
}

/**
 * Code Quality Fixer
 * Handles formatting, imports, and basic quality issues
 */
class CodeQualityFixer {
  constructor(options) {
    this.options = options;
  }

  async fix(content, issue, filePath) {
    const text = `${issue.title} ${issue.description}`.toLowerCase();

    if (text.includes("import")) {
      return this.organizeImports(content, filePath);
    }

    if (text.includes("type") && text.includes("annotation")) {
      return this.addTypeAnnotations(content, filePath);
    }

    if (text.includes("naming") || text.includes("convention")) {
      return this.fixNamingConventions(content, issue);
    }

    return { modified: false, reason: "No applicable code quality fix" };
  }

  organizeImports(content, filePath) {
    const ext = path.extname(filePath);

    if ([".js", ".ts", ".jsx", ".tsx"].includes(ext)) {
      return this.organizeJavaScriptImports(content);
    } else if (ext === ".py") {
      return this.organizePythonImports(content);
    }

    return {
      modified: false,
      reason: "Unsupported file type for import organization",
    };
  }

  organizeJavaScriptImports(content) {
    const lines = content.split("\n");
    const imports = [];
    const rest = [];
    let inImportSection = true;

    for (const line of lines) {
      if (
        line.trim().startsWith("import ") ||
        (line.trim().startsWith("const ") && line.includes("require("))
      ) {
        imports.push(line);
      } else if (line.trim() === "" && inImportSection) {
        // Keep empty lines in import section
        imports.push(line);
      } else {
        inImportSection = false;
        rest.push(line);
      }
    }

    if (imports.length > 0) {
      // Sort imports
      const sortedImports = imports
        .filter((line) => line.trim())
        .sort((a, b) => {
          // External packages first, then relative imports
          const aIsExternal = !a.includes("./") && !a.includes("../");
          const bIsExternal = !b.includes("./") && !b.includes("../");

          if (aIsExternal && !bIsExternal) return -1;
          if (!aIsExternal && bIsExternal) return 1;

          return a.localeCompare(b);
        });

      const newContent = [...sortedImports, "", ...rest].join("\n");

      if (newContent !== content) {
        return {
          modified: true,
          content: newContent,
          description: "Organized imports",
        };
      }
    }

    return { modified: false, reason: "Imports already organized" };
  }

  organizePythonImports(content) {
    const lines = content.split("\n");
    const standardImports = [];
    const thirdPartyImports = [];
    const localImports = [];
    const rest = [];
    let inImportSection = true;

    for (const line of lines) {
      const trimmed = line.trim();

      if (trimmed.startsWith("import ") || trimmed.startsWith("from ")) {
        // Categorize imports
        if (trimmed.includes(".")) {
          localImports.push(line);
        } else if (this.isStandardLibrary(trimmed)) {
          standardImports.push(line);
        } else {
          thirdPartyImports.push(line);
        }
      } else if (trimmed === "" && inImportSection) {
        // Keep empty lines in import section
      } else {
        inImportSection = false;
        rest.push(line);
      }
    }

    if (
      standardImports.length > 0 ||
      thirdPartyImports.length > 0 ||
      localImports.length > 0
    ) {
      const organizedImports = [
        ...standardImports.sort(),
        ...(thirdPartyImports.length > 0
          ? ["", ...thirdPartyImports.sort()]
          : []),
        ...(localImports.length > 0 ? ["", ...localImports.sort()] : []),
      ];

      const newContent = [...organizedImports, "", ...rest].join("\n");

      if (newContent !== content) {
        return {
          modified: true,
          content: newContent,
          description: "Organized Python imports",
        };
      }
    }

    return { modified: false, reason: "Python imports already organized" };
  }

  isStandardLibrary(importLine) {
    const standardLibs = [
      "os",
      "sys",
      "json",
      "datetime",
      "time",
      "math",
      "random",
      "collections",
      "itertools",
      "functools",
      "re",
      "urllib",
      "http",
    ];

    for (const lib of standardLibs) {
      if (importLine.includes(lib)) return true;
    }
    return false;
  }

  addTypeAnnotations(content, filePath) {
    if (!filePath.endsWith(".py")) {
      return {
        modified: false,
        reason: "Type annotations only supported for Python",
      };
    }

    const lines = content.split("\n");
    let modified = false;

    // Simple heuristic: add type hints to function parameters
    for (let i = 0; i < lines.length; i++) {
      const line = lines[i];
      const match = line.match(/def\s+(\w+)\s*\(([^)]*)\)/);

      if (match && !line.includes(":")) {
        const [, funcName, params] = match;

        if (params.trim() && !params.includes(":")) {
          // Add basic type hints
          const typedParams = params
            .split(",")
            .map((param) => {
              const trimmed = param.trim();
              if (trimmed && !trimmed.includes("=")) {
                return `${trimmed}: str`; // Default to str
              }
              return param;
            })
            .join(", ");

          lines[i] = line.replace(`(${params})`, `(${typedParams})`);
          modified = true;
        }
      }
    }

    if (modified) {
      return {
        modified: true,
        content: lines.join("\n"),
        description: "Added basic type annotations",
      };
    }

    return { modified: false, reason: "No type annotation improvements found" };
  }

  fixNamingConventions(content, issue) {
    // Simple camelCase to snake_case conversion for Python
    if (issue.file && issue.file.endsWith(".py")) {
      const lines = content.split("\n");
      let modified = false;

      for (let i = 0; i < lines.length; i++) {
        const line = lines[i];

        // Convert camelCase variable names to snake_case
        const camelCaseMatch = line.match(/(\w*[a-z])([A-Z]\w*)/g);
        if (camelCaseMatch) {
          let newLine = line;
          for (const match of camelCaseMatch) {
            const snakeCase = match.replace(/([A-Z])/g, "_$1").toLowerCase();
            newLine = newLine.replace(match, snakeCase);
          }

          if (newLine !== line) {
            lines[i] = newLine;
            modified = true;
          }
        }
      }

      if (modified) {
        return {
          modified: true,
          content: lines.join("\n"),
          description: "Fixed naming conventions (camelCase to snake_case)",
        };
      }
    }

    return { modified: false, reason: "No naming convention fixes needed" };
  }
}

/**
 * Security Fixer
 * Handles basic security improvements
 */
class SecurityFixer {
  constructor(options) {
    this.options = options;
  }

  async fix(content, issue, filePath) {
    const text = `${issue.title} ${issue.description}`.toLowerCase();

    if (text.includes("sql") && text.includes("injection")) {
      return this.fixSQLInjection(content, issue);
    }

    if (text.includes("password") || text.includes("secret")) {
      return this.fixHardcodedSecrets(content);
    }

    if (text.includes("input") && text.includes("validation")) {
      return this.addInputValidation(content, filePath);
    }

    return { modified: false, reason: "No applicable security fix" };
  }

  fixSQLInjection(content, issue) {
    const lines = content.split("\n");
    let modified = false;

    for (let i = 0; i < lines.length; i++) {
      const line = lines[i];

      // Look for string concatenation in SQL queries
      if (
        (line.includes("SELECT") && line.includes("${")) ||
        line.includes("+ ")
      ) {
        // Add comment suggesting parameterized queries
        const indentation = this.getIndentation(line);
        lines.splice(
          i,
          0,
          `${indentation}// TODO: Use parameterized queries to prevent SQL injection`
        );
        modified = true;
        break;
      }
    }

    if (modified) {
      return {
        modified: true,
        content: lines.join("\n"),
        description: "Added SQL injection prevention comment",
      };
    }

    return { modified: false, reason: "No SQL injection patterns found" };
  }

  fixHardcodedSecrets(content) {
    const lines = content.split("\n");
    let modified = false;

    for (let i = 0; i < lines.length; i++) {
      const line = lines[i];

      // Look for hardcoded passwords/secrets
      if (
        line.includes("password") &&
        line.includes("=") &&
        line.includes('"')
      ) {
        const indentation = this.getIndentation(line);
        lines.splice(
          i,
          0,
          `${indentation}// TODO: Move secret to environment variable`
        );
        modified = true;
        break;
      }
    }

    if (modified) {
      return {
        modified: true,
        content: lines.join("\n"),
        description: "Added hardcoded secret warning",
      };
    }

    return { modified: false, reason: "No hardcoded secrets found" };
  }

  addInputValidation(content, filePath) {
    // Add basic input validation patterns
    const ext = path.extname(filePath);

    if ([".js", ".ts"].includes(ext)) {
      const lines = content.split("\n");
      let modified = false;

      for (let i = 0; i < lines.length; i++) {
        const line = lines[i];

        if (line.includes("req.body") || line.includes("req.params")) {
          const indentation = this.getIndentation(line);
          lines.splice(
            i,
            0,
            `${indentation}// TODO: Add input validation here`
          );
          modified = true;
          break;
        }
      }

      if (modified) {
        return {
          modified: true,
          content: lines.join("\n"),
          description: "Added input validation reminder",
        };
      }
    }

    return {
      modified: false,
      reason: "No input validation improvements found",
    };
  }

  getIndentation(line) {
    const match = line.match(/^(\s*)/);
    return match ? match[1] : "";
  }
}

// CLI interface
if (require.main === module) {
  const args = process.argv.slice(2);

  if (args.length === 0) {
    console.log(
      "Usage: node auto-fix-engine.js <issues-file> [--dry-run] [--verbose] [--no-backup]"
    );
    process.exit(1);
  }

  const issuesFile = args[0];
  const options = {
    dryRun: args.includes("--dry-run"),
    verbose: args.includes("--verbose"),
    backup: !args.includes("--no-backup"),
  };

  const engine = new AutoFixEngine(options);

  try {
    const issuesData = JSON.parse(fs.readFileSync(issuesFile, "utf8"));
    const autoFixIssues = issuesData.categories?.autoFix || [];

    engine.applyFixes(autoFixIssues).then((results) => {
      console.log("\nüìä Auto-Fix Results:");
      console.log(`‚úÖ Applied: ${results.summary.applied}`);
      console.log(`‚è≠Ô∏è  Skipped: ${results.summary.skipped}`);
      console.log(`‚ùå Errors: ${results.summary.errors}`);
      console.log(`üìà Success Rate: ${results.summary.successRate}%`);

      if (options.verbose) {
        console.log("\nüìã Detailed Results:");
        console.log(JSON.stringify(results.details, null, 2));
      }
    });
  } catch (error) {
    console.error("Error running auto-fix engine:", error.message);
    process.exit(1);
  }
}

module.exports = AutoFixEngine;

