#!/usr/bin/env node

/**
 * Integration Test Suite
 * Tests complete workflows and agent coordination
 */

const fs = require("fs");
const path = require("path");
const { execSync } = require("child_process");

class IntegrationTest {
  constructor() {
    this.projectRoot = path.resolve(__dirname, "../..");
    this.results = {
      passed: [],
      failed: [],
      warnings: []
    };
  }

  async runAllTests() {
    console.log("üöÄ Integration Test Suite Started");
    console.log("=====================================");

    try {
      // Test core structure
      await this.testCoreStructure();

      // Test template system
      await this.testTemplateSystem();

      // Test commands integration
      await this.testCommandsIntegration();

      // Test agent coordination
      await this.testAgentCoordination();

      // Test hooks system
      await this.testHooksSystem();

      this.printResults();

    } catch (error) {
      console.error("‚ùå Integration Test Suite Failed:", error.message);
      process.exit(1);
    }
  }

  async testCoreStructure() {
    console.log("\nüìÅ Testing Core Structure...");

    const requiredPaths = [
      ".claude/commands",
      ".claude/agents", 
      ".claude/tools",
      ".claude/hooks",
      ".claude/settings.json",
      "templates/builders",
      "CLAUDE.md",
      "ROADMAP.md"
    ];

    for (const reqPath of requiredPaths) {
      const fullPath = path.join(this.projectRoot, reqPath);
      if (fs.existsSync(fullPath)) {
        this.results.passed.push(`Core structure: ${reqPath} exists`);
        console.log(`  ‚úì ${reqPath}`);
      } else {
        this.results.failed.push(`Core structure: ${reqPath} missing`);
        console.log(`  ‚ùå ${reqPath}`);
