#!/usr/bin/env node

/**
 * Regression Test Suite
 * Ensures no functionality lost in optimization process
 */

const fs = require("fs");
const path = require("path");

class RegressionTest {
  constructor() {
    this.projectRoot = path.resolve(__dirname, "../..");
    this.results = {
      functionality: [],
      performance: [],
      compatibility: [],
      issues: []
    };
  }

  async runRegressionTests() {
    console.log("üîç Regression Test Suite Started");
    console.log("==================================");

    try {
      // Test preserved functionality
      await this.testPreservedFunctionality();

      // Test performance maintained
      await this.testPerformanceMaintained();

      // Test backward compatibility
      await this.testBackwardCompatibility();

      // Test no functionality lost
      await this.testNoFunctionalityLost();

      this.printRegressionResults();

    } catch (error) {
      console.error("‚ùå Regression Test Suite Failed:", error.message);
      process.exit(1);
    }
  }

  async testPreservedFunctionality() {
    console.log("\nüîß Testing Preserved Functionality...");

    const coreFeatures = [
      {
        name: "Template System",
        test: () => this.validateTemplateSystem(),
        critical: true
      },
      {
        name: "Command Structure", 
        test: () => this.validateCommandStructure(),
        critical: true
      },
      {
        name: "Agent System",
        test: () => this.validateAgentSystem(), 
        critical: true
      },
      {
        name: "Hook Integration",
        test: () => this.validateHookIntegration(),
        critical: true
      },
      {
        name: "Settings Configuration",
        test: () => this.validateSettingsConfiguration(),
        critical: true
      }
    ];

    for (const feature of coreFeatures) {
      try {
        const result = await feature.test();
        if (result.success) {
          this.results.functionality.push(`‚úÖ ${feature.name}: ${result.message}`);
          console.log(`  ‚úÖ ${feature.name}: ${result.message}`);
        } else {
          const severity = feature.critical ? "CRITICAL" : "WARNING";
          this.results.issues.push(`${severity}: ${feature.name}: ${result.message}`);
          console.log(`  ‚ùå ${feature.name}: ${result.message}`);
        }
      } catch (error) {
        this.results.issues.push(`ERROR: ${feature.name}: ${error.message}`);
        console.log(`  ‚ùå ${feature.name}: Error - ${error.message}`);
      }
    }
  }

  async testPerformanceMaintained() {
    console.log("\n‚ö° Testing Performance Maintained...");

    const performanceTests = [
      {
        name: "File Access Speed",
        test: () => this.testFileAccessSpeed()
      },
      {
        name: "Memory Usage",
        test: () => this.testMemoryUsage()
      },
      {
        name: "Hook Execution Time",
        test: () => this.testHookExecutionTime()
      }
    ];

    for (const test of performanceTests) {
      try {
        const result = await test.test();
        this.results.performance.push(`‚ö° ${test.name}: ${result.message}`);
        console.log(`  ‚ö° ${test.name}: ${result.message}`);
      } catch (error) {
        this.results.issues.push(`PERFORMANCE: ${test.name}: ${error.message}`);
        console.log(`  ‚ö†Ô∏è ${test.name}: ${error.message}`);
      }
    }
  }

  async testBackwardCompatibility() {
    console.log("\nüîÑ Testing Backward Compatibility...");

    const compatibilityTests = [
      {
        name: "Legacy Path Access",
        test: () => this.testLegacyPathAccess()
      },
      {
        name: "Template Format Support", 
        test: () => this.testTemplateFormatSupport()
      },
      {
        name: "Configuration Migration",
        test: () => this.testConfigurationMigration()
      }
    ];

    for (const test of compatibilityTests) {
      try {
        const result = await test.test();
        this.results.compatibility.push(`üîÑ ${test.name}: ${result.message}`);
        console.log(`  üîÑ ${test.name}: ${result.message}`);
      } catch (error) {
        this.results.issues.push(`COMPATIBILITY: ${test.name}: ${error.message}`);
        console.log(`  ‚ö†Ô∏è ${test.name}: ${error.message}`);
      }
    }
  }

  async testNoFunctionalityLost() {
    console.log("\nüîí Testing No Functionality Lost...");

    // Compare before/after feature matrix
    const originalFeatures = [
      "Template variable substitution",
      "PRD document generation", 
      "Technical design templates",
      "API specification builders",
      "Code review automation",
      "Auto-fix capabilities",
      "Agent orchestration",
      "Hook system automation",
      "Enterprise formatting",
      "Modular composition"
    ];

    for (const feature of originalFeatures) {
      const isPresent = this.checkFeaturePresent(feature);
      if (isPresent.success) {
        console.log(`  ‚úÖ ${feature}: Present and functional`);
      } else {
        this.results.issues.push(`LOST FUNCTIONALITY: ${feature}: ${isPresent.message}`);
        console.log(`  ‚ùå ${feature}: ${isPresent.message}`);
      }
    }
  }

  // Validation methods
  validateTemplateSystem() {
    const buildersPath = path.join(this.projectRoot, "templates/builders");
    if (!fs.existsSync(buildersPath)) {
      return { success: false, message: "Templates/builders directory missing" };
    }

    const builders = fs.readdirSync(buildersPath);
    const requiredBuilders = ["prd-builder.md", "technical-design-builder.md", "api-specification-builder.md"];

    for (const builder of requiredBuilders) {
      if (!builders.includes(builder)) {
        return { success: false, message: `Missing builder: ${builder}` };
      }
    }

    return { success: true, message: "All template builders present and accessible" };
  }

  validateCommandStructure() {
    const commandsPath = path.join(this.projectRoot, ".claude/commands");
    if (!fs.existsSync(commandsPath)) {
      return { success: false, message: "Commands directory missing" };
    }

    const reviewPath = path.join(commandsPath, "review.md");
    if (!fs.existsSync(reviewPath)) {
      return { success: false, message: "Review command missing" };
    }

    const content = fs.readFileSync(reviewPath, 'utf8');
    if (!content.includes('--auto') || !content.includes('--fix')) {
      return { success: false, message: "Review command missing automation features" };
    }

    return { success: true, message: "Commands properly structured with automation features" };
  }

  validateAgentSystem() {
    const agentsPath = path.join(this.projectRoot, ".claude/agents");
    if (!fs.existsSync(agentsPath)) {
      return { success: false, message: "Agents directory missing" };
    }

    const requiredAgents = ["auto-fix-specialist.md", "template-processor.md"];
    const agents = fs.readdirSync(agentsPath);

    for (const agent of requiredAgents) {
      if (!agents.includes(agent)) {
        return { success: false, message: `Missing agent: ${agent}` };
      }
    }

    return { success: true, message: "All required agents present and properly structured" };
  }

  validateHookIntegration() {
    const hooksPath = path.join(this.projectRoot, ".claude/hooks");
    if (!fs.existsSync(hooksPath)) {
      return { success: false, message: "Hooks directory missing" };
    }

    const sessionStartPath = path.join(hooksPath, "session-start.sh");
    if (!fs.existsSync(sessionStartPath)) {
      return { success: false, message: "Session start hook missing" };
    }

    // Check if hook is executable (Unix-like systems)
    try {
      const stats = fs.statSync(sessionStartPath);
      // Basic existence check (executable check would be platform-specific)
      return { success: true, message: "Hooks properly configured and accessible" };
    } catch (error) {
      return { success: false, message: `Hook access error: ${error.message}` };
    }
  }

  validateSettingsConfiguration() {
    const settingsPath = path.join(this.projectRoot, ".claude/settings.json");
    if (!fs.existsSync(settingsPath)) {
      return { success: false, message: "Settings file missing" };
    }

    try {
      const settings = JSON.parse(fs.readFileSync(settingsPath, 'utf8'));

      // Check critical settings
      if (!settings.permissions) {
        return { success: false, message: "Permissions configuration missing" };
      }

      if (!settings.agents) {
        return { success: false, message: "Agents configuration missing" };
      }

      if (!settings.hooks || !settings.hooks.enabled) {
        return { success: false, message: "Hooks not enabled in configuration" };
      }

      return { success: true, message: "Settings properly configured with all required sections" };
    } catch (error) {
