      return { 
        accurate: false, 
        message: "Phase information outdated" 
      };
    }

    // Check for recent accomplishments
    if (!content.includes('Claude Code native patterns')) {
      return { 
        accurate: false, 
        message: "Recent accomplishments not documented" 
      };
    }

    return { 
      accurate: true, 
      message: "Current and comprehensive" 
    };
  }

  validateReadmeFile() {
    const readmePath = path.join(this.projectRoot, "README.md");
    if (!fs.existsSync(readmePath)) {
      return { 
        accurate: false, 
        message: "README.md missing - should provide project overview" 
      };
    }

    const content = fs.readFileSync(readmePath, 'utf8');

    // Check for basic project information
    if (!content.includes('VS Code workspace') || !content.includes('multi-language')) {
      return { 
        accurate: false, 
        message: "Project description incomplete" 
      };
    }

    return { 
      accurate: true, 
      message: "Adequate project overview" 
    };
  }

  validateReviewCommand() {
    const reviewPath = path.join(this.projectRoot, ".claude/commands/review.md");
    if (!fs.existsSync(reviewPath)) {
      throw new Error("Review command documentation not found");
    }

    const content = fs.readFileSync(reviewPath, 'utf8');

    // Check for automation features documentation
    const requiredFeatures = [
      '--auto',
      '--fix', 
      '--implement',
      'Auto-Fix Engine',
      'Implementation Enhancement'
    ];

    const missingFeatures = requiredFeatures.filter(feature => !content.includes(feature));

    if (missingFeatures.length > 0) {
      return { 
        accurate: false, 
        message: `Missing feature documentation: ${missingFeatures.join(', ')}` 
      };
    }

    return { 
      accurate: true, 
      message: "Comprehensive command documentation" 
    };
  }

  // Technical accuracy checks
  checkDirectoryReferences() {
    const claudePath = path.join(this.projectRoot, "CLAUDE.md");
    const content = fs.readFileSync(claudePath, 'utf8');

    // Check if documented structure matches actual structure
    const documentedPaths = [
      '.claude/',
      '.vscode/',
      'src/',
      'docs/',
      'tests/',
      'projects/main/',
      'templates/builders'
    ];

    for (const docPath of documentedPaths) {
      if (content.includes(docPath)) {
        const actualPath = path.join(this.projectRoot, docPath.replace('/', ''));
        if (!fs.existsSync(actualPath)) {
          return { 
            accurate: false, 
            message: `Documented path doesn't exist: ${docPath}` 
          };
        }
      }
    }

    return { 
      accurate: true, 
      message: "Directory references match actual structure" 
    };
  }

  checkCommandDocumentation() {
    const commandsDir = path.join(this.projectRoot, ".claude/commands");
    if (!fs.existsSync(commandsDir)) {
      return { 
        accurate: false, 
        message: "Commands directory not found" 
      };
    }

    const commands = fs.readdirSync(commandsDir);

    // Check if CLAUDE.md references match actual commands
    const claudePath = path.join(this.projectRoot, "CLAUDE.md");
    const claudeContent = fs.readFileSync(claudePath, 'utf8');

    for (const command of commands) {
      const commandName = command.replace('.md', '');
      if (!claudeContent.includes(commandName)) {
        return { 
          accurate: false, 
          message: `Command ${commandName} not documented in CLAUDE.md` 
        };
      }
    }

    return { 
      accurate: true, 
      message: "Command documentation accurate" 
    };
  }

  checkAgentSpecifications() {
    const agentsDir = path.join(this.projectRoot, ".claude/agents");
    if (!fs.existsSync(agentsDir)) {
      return { 
        accurate: false, 
        message: "Agents directory not found" 
      };
    }

    const agents = fs.readdirSync(agentsDir);
    const settingsPath = path.join(this.projectRoot, ".claude/settings.json");

    if (!fs.existsSync(settingsPath)) {
      return { 
        accurate: false, 
        message: "Settings file missing for agent configuration validation" 
      };
    }

    const settings = JSON.parse(fs.readFileSync(settingsPath, 'utf8'));

    // Check if agents in directory match settings configuration
    for (const agent of agents) {
      const agentName = agent.replace('.md', '').replace('-', '_');
      if (!settings.agents || !settings.agents[agentName.replace('_', '-')]) {
        return { 
          accurate: false, 
          message: `Agent ${agentName} not configured in settings` 
        };
      }
    }

    return { 
      accurate: true, 
      message: "Agent specifications consistent" 
    };
  }

  checkTemplateReferences() {
    const claudePath = path.join(this.projectRoot, "CLAUDE.md");
    const content = fs.readFileSync(claudePath, 'utf8');

    // Check if template references match actual templates
    const buildersDir = path.join(this.projectRoot, "templates/builders");
    const builders = fs.readdirSync(buildersDir);

    const documentedTemplates = [
      'prd-builder.md',
      'technical-design-builder.md',
      'api-specification-builder.md'
    ];

    for (const template of documentedTemplates) {
      if (!builders.includes(template)) {
        return { 
          accurate: false, 
          message: `Documented template missing: ${template}` 
        };
      }
    }

    return { 
      accurate: true, 
      message: "Template references accurate" 
    };
  }

  checkConfigurationSettings() {
    const settingsPath = path.join(this.projectRoot, ".claude/settings.json");
    if (!fs.existsSync(settingsPath)) {
      return { 
        accurate: false, 
        message: "Settings file missing" 
      };
    }

    const settings = JSON.parse(fs.readFileSync(settingsPath, 'utf8'));
    const claudePath = path.join(this.projectRoot, "CLAUDE.md");
    const claudeContent = fs.readFileSync(claudePath, 'utf8');

    // Check if documented features match settings
    if (claudeContent.includes('permissions') && !settings.permissions) {
      return { 
        accurate: false, 
        message: "Permissions documentation doesn't match settings" 
      };
    }

    if (claudeContent.includes('hooks') && !settings.hooks) {
      return { 
        accurate: false, 
        message: "Hooks documentation doesn't match settings" 
      };
    }

    return { 
      accurate: true, 
      message: "Configuration documentation accurate" 
    };
  }

  // Completeness checks
  checkAllFeaturesDocumented() {
    const features = [
      'Template System',
      'Command Ecosystem', 
      'Agent System',
      'Hook Integration',
      'Auto-fix Engine',
      'Variable Substitution'
    ];

    const claudePath = path.join(this.projectRoot, "CLAUDE.md");
    const content = fs.readFileSync(claudePath, 'utf8');

    const undocumentedFeatures = features.filter(feature => 
      !content.toLowerCase().includes(feature.toLowerCase())
    );

    if (undocumentedFeatures.length > 0) {
      return { 
        complete: false, 
        message: `Undocumented features: ${undocumentedFeatures.join(', ')}` 
      };
    }

    return { 
      complete: true, 
      message: "All major features documented" 
    };
  }

  checkSetupInstructions() {
    const claudePath = path.join(this.projectRoot, "CLAUDE.md");
    const content = fs.readFileSync(claudePath, 'utf8');

    const setupSections = [
      'Getting Started',
      'VS Code', 
      'extensions',
      'workspace'
    ];

    const missingSetup = setupSections.filter(section => 
      !content.toLowerCase().includes(section.toLowerCase())
    );

    if (missingSetup.length > 0) {
      return { 
        complete: false, 
        message: `Incomplete setup instructions: ${missingSetup.join(', ')}` 
      };
    }

    return { 
      complete: true, 
      message: "Setup instructions complete" 
    };
  }

  checkUsageExamples() {
    const reviewPath = path.join(this.projectRoot, ".claude/commands/review.md");
    const content = fs.readFileSync(reviewPath, 'utf8');

    // Check for usage examples
    const hasExamples = content.includes('```bash') || content.includes('Example usage');

    if (!hasExamples) {
      return { 
        complete: false, 
        message: "Missing usage examples in command documentation" 
      };
    }

    return { 
      complete: true, 
      message: "Usage examples present" 
    };
  }

  checkArchitectureDocumentation() {
    const claudePath = path.join(this.projectRoot, "CLAUDE.md");
    const content = fs.readFileSync(claudePath, 'utf8');

    // Check for architecture section
    if (!content.includes('Architecture') && !content.includes('structure')) {
      return { 
        complete: false, 
        message: "Architecture documentation missing" 
      };
    }

    return { 
      complete: true, 
      message: "Architecture adequately documented" 
    };
  }

  // Consistency checks
  checkCrossReferences() {
    // Check if references between files are accurate
    const files = ['CLAUDE.md', 'ROADMAP.md'];

    for (const file of files) {
      const filePath = path.join(this.projectRoot, file);
      if (fs.existsSync(filePath)) {
        const content = fs.readFileSync(filePath, 'utf8');

        // Check for broken internal references
        const references = content.match(/\[.*?\]\(.*?\)/g) || [];

        for (const ref of references) {
          const linkMatch = ref.match(/\]\(([^)]+)\)/);
          if (linkMatch && linkMatch[1].startsWith('./')) {
            const refPath = path.join(this.projectRoot, linkMatch[1]);
            if (!fs.existsSync(refPath)) {
              return { 
                consistent: false, 
                message: `Broken reference in ${file}: ${linkMatch[1]}` 
              };
            }
          }
        }
      }
    }

    return { 
      consistent: true, 
      message: "Cross-references accurate" 
    };
  }

  checkVersionSync() {
    const roadmapPath = path.join(this.projectRoot, "ROADMAP.md");
    const claudePath = path.join(this.projectRoot, "CLAUDE.md");

    if (!fs.existsSync(roadmapPath) || !fs.existsSync(claudePath)) {
      return { 
        consistent: false, 
        message: "Cannot verify version sync - files missing" 
      };
    }

    const roadmapContent = fs.readFileSync(roadmapPath, 'utf8');
    const claudeContent = fs.readFileSync(claudePath, 'utf8');

    // Check if current phase information is consistent
    const roadmapPhase = roadmapContent.match(/Phase (\d+\.\d+)/g)?.pop();
    const claudePhase = claudeContent.match(/Phase (\d+\.\d+)/g)?.pop();

    if (roadmapPhase && claudePhase && roadmapPhase !== claudePhase) {
      return { 
        consistent: false, 
        message: `Version mismatch: ROADMAP shows ${roadmapPhase}, CLAUDE shows ${claudePhase}` 
      };
    }

    return { 
      consistent: true, 
      message: "Version information synchronized" 
    };
  }

  checkTerminologyConsistency() {
    const files = ['CLAUDE.md', 'ROADMAP.md', '.claude/commands/review.md'];
    const terms = new Map();

    for (const file of files) {
      const filePath = path.join(this.projectRoot, file);
      if (fs.existsSync(filePath)) {
        const content = fs.readFileSync(filePath, 'utf8');

        // Check for consistent terminology
        const keyTerms = [
          'template system',
          'auto-fix',
          'agent',
          'hook',
          'Claude Code'
        ];

        for (const term of keyTerms) {
          const variations = [
            term,
            term.replace(' ', '-'),
            term.replace(' ', '_'),
            term.replace(' ', '')
          ];

          const found = variations.filter(variant => 
            content.toLowerCase().includes(variant.toLowerCase())
          );

          if (found.length > 1) {
            if (!terms.has(term)) terms.set(term, new Set());
            found.forEach(variant => terms.get(term).add(variant));
          }
        }
      }
    }

    // Check for inconsistent terminology usage
    for (const [term, variations] of terms) {
      if (variations.size > 2) {
        return { 
          consistent: false, 
          message: `Inconsistent terminology for "${term}": ${Array.from(variations).join(', ')}` 
        };
      }
    }

    return { 
      consistent: true, 
      message: "Terminology usage consistent" 
    };
  }

  printValidationResults() {
    console.log("\nüìä Documentation Validation Results");
    console.log("====================================");

    console.log(`\n‚úÖ Accurate Documentation: ${this.results.accurate.length}`);
    this.results.accurate.forEach(result => console.log(`  ${result}`));

    if (this.results.warnings.length > 0) {
      console.log(`\n‚ö†Ô∏è Warnings: ${this.results.warnings.length}`);
      this.results.warnings.forEach(warning => console.log(`  ‚ö†Ô∏è ${warning}`));
    }

    if (this.results.outdated.length > 0) {
      console.log(`\nüìù Outdated Documentation: ${this.results.outdated.length}`);
      this.results.outdated.forEach(outdated => console.log(`  üìù ${outdated}`));
    }

    if (this.results.missing.length > 0) {
      console.log(`\n‚ùå Missing Documentation: ${this.results.missing.length}`);
      this.results.missing.forEach(missing => console.log(`  ‚ùå ${missing}`));
    }

    const totalChecks = this.results.accurate.length + this.results.outdated.length + 
                       this.results.missing.length + this.results.warnings.length;
    const accuracyRate = Math.round((this.results.accurate.length / totalChecks) * 100);

    console.log(`\nüìà Documentation Quality:`);
    console.log(`  ‚Ä¢ Total checks: ${totalChecks}`);
    console.log(`  ‚Ä¢ Accurate: ${this.results.accurate.length}`);
    console.log(`  ‚Ä¢ Needs update: ${this.results.outdated.length}`);
    console.log(`  ‚Ä¢ Missing: ${this.results.missing.length}`);
    console.log(`  ‚Ä¢ Accuracy rate: ${accuracyRate}%`);

    if (this.results.missing.length === 0 && this.results.outdated.length === 0) {
      console.log("\nüéâ Documentation is complete and accurate!");
      return true;
    } else {
      console.log(`\nüìù Documentation needs ${this.results.outdated.length + this.results.missing.length} updates`);
      return false;
    }
  }
}

// CLI execution
if (require.main === module) {
  const validator = new DocumentationValidator();
  validator.validateAllDocumentation()
    .then(success => {
      process.exit(success ? 0 : 1);
    })
    .catch(error => {
      console.error("Documentation validation failed:", error);
      process.exit(1);
    });
}

module.exports = DocumentationValidator;

