  // Core file validators
  validateClaudeFile() {
    const claudePath = path.join(this.projectRoot, "CLAUDE.md");
    if (!fs.existsSync(claudePath)) {
      throw new Error("CLAUDE.md file not found");
    }

    const content = fs.readFileSync(claudePath, 'utf8');

    // Check for critical sections
    const requiredSections = [
      'Repository Overview',
      'Project Structure', 
      'Development Environment',
      'Common Development Commands',
      'Architecture Patterns',
      'Key Features',
      'Claude Code Behavioral Guidelines'
    ];

    const missingSections = requiredSections.filter(section => !content.includes(section));

    if (missingSections.length > 0) {
      return { 
        accurate: false, 
        message: `Missing sections: ${missingSections.join(', ')}` 
      };
    }

    // Check for current structure references
    if (!content.includes('.claude/') || !content.includes('templates/builders')) {
      return { 
