  // Technical accuracy checks
  checkDirectoryReferences() {
    const claudePath = path.join(this.projectRoot, "CLAUDE.md");
    const content = fs.readFileSync(claudePath, 'utf8');

    // Check if documented structure matches actual structure
    const documentedPaths = [
      '.claude/',
      '.vscode/',
      'src/',
      'docs/',
      'tests/',
      'projects/main/',
      'templates/builders'
    ];

    for (const docPath of documentedPaths) {
      if (content.includes(docPath)) {
        const actualPath = path.join(this.projectRoot, docPath.replace('/', ''));
        if (!fs.existsSync(actualPath)) {
          return { 
            accurate: false, 
            message: `Documented path doesn't exist: ${docPath}` 
          };
        }
      }
    }

    return { 
      accurate: true, 
      message: "Directory references match actual structure" 
    };
  }

  checkCommandDocumentation() {
    const commandsDir = path.join(this.projectRoot, ".claude/commands");
    if (!fs.existsSync(commandsDir)) {
      return { 
        accurate: false, 
        message: "Commands directory not found" 
      };
    }

    const commands = fs.readdirSync(commandsDir);

    // Check if CLAUDE.md references match actual commands
    const claudePath = path.join(this.projectRoot, "CLAUDE.md");
    const claudeContent = fs.readFileSync(claudePath, 'utf8');

    for (const command of commands) {
      const commandName = command.replace('.md', '');
      if (!claudeContent.includes(commandName)) {
        return { 
          accurate: false, 
          message: `Command ${commandName} not documented in CLAUDE.md` 
        };
      }
    }

    return { 
      accurate: true, 
      message: "Command documentation accurate" 
    };
  }

  checkAgentSpecifications() {
    const agentsDir = path.join(this.projectRoot, ".claude/agents");
    if (!fs.existsSync(agentsDir)) {
      return { 
        accurate: false, 
        message: "Agents directory not found" 
      };
    }

    const agents = fs.readdirSync(agentsDir);
    const settingsPath = path.join(this.projectRoot, ".claude/settings.json");

    if (!fs.existsSync(settingsPath)) {
      return { 
        accurate: false, 
        message: "Settings file missing for agent configuration validation" 
      };
    }

    const settings = JSON.parse(fs.readFileSync(settingsPath, 'utf8'));

    // Check if agents in directory match settings configuration
    for (const agent of agents) {
      const agentName = agent.replace('.md', '').replace('-', '_');
      if (!settings.agents || !settings.agents[agentName.replace('_', '-')]) {
        return { 
          accurate: false, 
          message: `Agent ${agentName} not configured in settings` 
        };
      }
    }

    return { 
      accurate: true, 
      message: "Agent specifications consistent" 
    };
  }

  checkTemplateReferences() {
    const claudePath = path.join(this.projectRoot, "CLAUDE.md");
    const content = fs.readFileSync(claudePath, 'utf8');

    // Check if template references match actual templates
    const buildersDir = path.join(this.projectRoot, "templates/builders");
    const builders = fs.readdirSync(buildersDir);

    const documentedTemplates = [
      'prd-builder.md',
      'technical-design-builder.md',
      'api-specification-builder.md'
