    };
  }

  // Consistency checks
  checkCrossReferences() {
    // Check if references between files are accurate
    const files = ['CLAUDE.md', 'ROADMAP.md'];

    for (const file of files) {
      const filePath = path.join(this.projectRoot, file);
      if (fs.existsSync(filePath)) {
        const content = fs.readFileSync(filePath, 'utf8');

        // Check for broken internal references
        const references = content.match(/\[.*?\]\(.*?\)/g) || [];

        for (const ref of references) {
          const linkMatch = ref.match(/\]\(([^)]+)\)/);
          if (linkMatch && linkMatch[1].startsWith('./')) {
            const refPath = path.join(this.projectRoot, linkMatch[1]);
            if (!fs.existsSync(refPath)) {
              return { 
                consistent: false, 
                message: `Broken reference in ${file}: ${linkMatch[1]}` 
              };
            }
          }
        }
      }
    }

    return { 
      consistent: true, 
      message: "Cross-references accurate" 
    };
  }

  checkVersionSync() {
    const roadmapPath = path.join(this.projectRoot, "ROADMAP.md");
    const claudePath = path.join(this.projectRoot, "CLAUDE.md");

    if (!fs.existsSync(roadmapPath) || !fs.existsSync(claudePath)) {
      return { 
        consistent: false, 
        message: "Cannot verify version sync - files missing" 
      };
    }

    const roadmapContent = fs.readFileSync(roadmapPath, 'utf8');
    const claudeContent = fs.readFileSync(claudePath, 'utf8');

    // Check if current phase information is consistent
    const roadmapPhase = roadmapContent.match(/Phase (\d+\.\d+)/g)?.pop();
    const claudePhase = claudeContent.match(/Phase (\d+\.\d+)/g)?.pop();

    if (roadmapPhase && claudePhase && roadmapPhase !== claudePhase) {
      return { 
        consistent: false, 
        message: `Version mismatch: ROADMAP shows ${roadmapPhase}, CLAUDE shows ${claudePhase}` 
      };
    }

    return { 
      consistent: true, 
      message: "Version information synchronized" 
    };
  }

  checkTerminologyConsistency() {
    const files = ['CLAUDE.md', 'ROADMAP.md', '.claude/commands/review.md'];
    const terms = new Map();

    for (const file of files) {
      const filePath = path.join(this.projectRoot, file);
      if (fs.existsSync(filePath)) {
        const content = fs.readFileSync(filePath, 'utf8');

        // Check for consistent terminology
        const keyTerms = [
          'template system',
          'auto-fix',
          'agent',
          'hook',
          'Claude Code'
        ];

        for (const term of keyTerms) {
          const variations = [
            term,
            term.replace(' ', '-'),
            term.replace(' ', '_'),
            term.replace(' ', '')
          ];

          const found = variations.filter(variant => 
            content.toLowerCase().includes(variant.toLowerCase())
          );

          if (found.length > 1) {
            if (!terms.has(term)) terms.set(term, new Set());
            found.forEach(variant => terms.get(term).add(variant));
          }
        }
      }
    }

    // Check for inconsistent terminology usage
    for (const [term, variations] of terms) {
      if (variations.size > 2) {
        return { 
          consistent: false, 
          message: `Inconsistent terminology for "${term}": ${Array.from(variations).join(', ')}` 
        };
      }
    }

    return { 
      consistent: true, 
      message: "Terminology usage consistent" 
    };
  }

  printValidationResults() {
    console.log("\nüìä Documentation Validation Results");
    console.log("====================================");

    console.log(`\n‚úÖ Accurate Documentation: ${this.results.accurate.length}`);
    this.results.accurate.forEach(result => console.log(`  ${result}`));

    if (this.results.warnings.length > 0) {
      console.log(`\n‚ö†Ô∏è Warnings: ${this.results.warnings.length}`);
      this.results.warnings.forEach(warning => console.log(`  ‚ö†Ô∏è ${warning}`));
    }

    if (this.results.outdated.length > 0) {
      console.log(`\nüìù Outdated Documentation: ${this.results.outdated.length}`);
      this.results.outdated.forEach(outdated => console.log(`  üìù ${outdated}`));
    }

    if (this.results.missing.length > 0) {
      console.log(`\n‚ùå Missing Documentation: ${this.results.missing.length}`);
      this.results.missing.forEach(missing => console.log(`  ‚ùå ${missing}`));
    }

    const totalChecks = this.results.accurate.length + this.results.outdated.length + 
                       this.results.missing.length + this.results.warnings.length;
    const accuracyRate = Math.round((this.results.accurate.length / totalChecks) * 100);

    console.log(`\nüìà Documentation Quality:`);
    console.log(`  ‚Ä¢ Total checks: ${totalChecks}`);
    console.log(`  ‚Ä¢ Accurate: ${this.results.accurate.length}`);
    console.log(`  ‚Ä¢ Needs update: ${this.results.outdated.length}`);
    console.log(`  ‚Ä¢ Missing: ${this.results.missing.length}`);
    console.log(`  ‚Ä¢ Accuracy rate: ${accuracyRate}%`);

    if (this.results.missing.length === 0 && this.results.outdated.length === 0) {
      console.log("\nüéâ Documentation is complete and accurate!");
      return true;
    } else {
      console.log(`\nüìù Documentation needs ${this.results.outdated.length + this.results.missing.length} updates`);
      return false;
    }
  }
}

