  private async applyCustomizations(
    template: Template,
    customizations: ApplicableCustomization[]
  ): Promise<string> {
    let content = template.content;

    for (const customization of customizations) {
      switch (customization.type) {
        case 'naming_convention':
          content = this.applyNamingConvention(content, customization);
          break;
        case 'architecture_pattern':
          content = this.applyArchitecturePattern(content, customization);
          break;
        case 'coding_standard':
          content = this.applyCodingStandard(content, customization);
          break;
        case 'integration_pattern':
          content = this.applyIntegrationPattern(content, customization);
          break;
      }
    }

    return content;
  }
}
