      success_rate_by_category: this.calculateSuccessRateByCategory(sessions),
      average_implementation_time: this.calculateAverageImplementationTime(sessions),
      common_failure_patterns: this.identifyCommonFailurePatterns(sessions),
      automation_effectiveness: this.calculateAutomationEffectiveness(sessions),
      template_usage_statistics: this.analyzeTemplateUsage(sessions),
      verification_bottlenecks: this.identifyVerificationBottlenecks(sessions),
      recommendation_quality_trends: this.analyzeRecommendationQuality(sessions)
    };
  }

  private calculateSuccessRateByCategory(
    sessions: ProgressTrackingSession[]
  ): Map<RecommendationCategory, SuccessRate> {
    const categoryStats = new Map<RecommendationCategory, CategoryStats>();

    for (const session of sessions) {
      for (const recommendation of session.recommendations) {
        const stats = categoryStats.get(recommendation.category) || new CategoryStats();
        stats.total++;
        if (recommendation.status === 'completed') {
          stats.successful++;
        }
        categoryStats.set(recommendation.category, stats);
      }
    }

    return new Map(
      Array.from(categoryStats.entries()).map(([category, stats]) => [
        category,
        {
          rate: stats.successful / stats.total,
          confidence: this.calculateConfidence(stats),
          sample_size: stats.total
        }
      ])
    );
  }
}
