#!/bin/bash
# Enhanced post-tool-use.sh for RDIS integration

HOOK_START=$(date +%s%N)

# RDIS Integration Point
if [[ "$TOOL" == "review" || "$CONTENT" =~ /review ]]; then
    echo " Activating Review-Driven Implementation System..."

    # Extract review output
    REVIEW_OUTPUT_FILE="/tmp/rdis_review_output_$(date +%s).md"
    echo "$CONTENT" > "$REVIEW_OUTPUT_FILE"

    # Trigger RDIS processing
    ./modules/rdis/cli/process-review.sh \
        --input="$REVIEW_OUTPUT_FILE" \
        --session-id="$CLAUDE_SESSION_ID" \
        --auto-execute="${RDIS_AUTO_EXECUTE:-false}" \
        --verification-level="${RDIS_VERIFICATION_LEVEL:-standard}"

    RDIS_RESULT=$?

    if [ $RDIS_RESULT -eq 0 ]; then
        echo " RDIS processing completed successfully"
        ./modules/rdis/cli/status-summary.sh --session-id="$CLAUDE_SESSION_ID"
    else
        echo " RDIS processing encountered errors"
        ./modules/rdis/cli/error-report.sh --session-id="$CLAUDE_SESSION_ID"
    fi

    # Cleanup temporary files
    rm -f "$REVIEW_OUTPUT_FILE"
fi

# Performance monitoring
HOOK_END=$(date +%s%N)
DURATION=$(( (HOOK_END - HOOK_START) / 1000000 ))

if [ "$CLAUDE_DEBUG" = "true" ]; then
    echo " Post-tool-use hook execution: ${DURATION}ms" >&2
fi
