process_rdis_integration() {
    log_debug "Checking for RDIS integration triggers"
    log_debug "Tool: $TOOL"
    log_debug "Content contains /review: $(echo "$CONTENT" | grep -q "/review" && echo "yes" || echo "no")"

    # Check if this is a review command execution
    if [[ "$TOOL" == "review" ]] || echo "$CONTENT" | grep -q "/review"; then
        log_info "Detected review command execution - activating RDIS"

        # Create temporary review output file
        REVIEW_OUTPUT_FILE="/tmp/rdis_review_output_$(date +%s).md"

        # Extract review content
        if [[ -n "${OUTPUT_FILE:-}" ]] && [[ -f "$OUTPUT_FILE" ]]; then
            cp "$OUTPUT_FILE" "$REVIEW_OUTPUT_FILE"
        else
