        fi

        log_debug "Review output saved to: $REVIEW_OUTPUT_FILE"

        # Execute RDIS processing
        execute_rdis_processing "$REVIEW_OUTPUT_FILE"

        # Cleanup
        rm -f "$REVIEW_OUTPUT_FILE"
    fi
}

# RDIS Processing Execution
execute_rdis_processing() {
    local review_file="$1"

    log_info "Starting RDIS processing pipeline"

    # Generate session ID
    local session_id="${CLAUDE_SESSION_ID:-rdis_$(date +%s)}"

    # Build command arguments
    local rdis_args=(
        "--input=$review_file"
        "--session-id=$session_id"
        "--verification-level=$RDIS_VERIFICATION_LEVEL"
    )

    if [[ "$RDIS_AUTO_EXECUTE" == "true" ]]; then
        rdis_args+=("--auto-execute")
    fi

    # Execute RDIS processing
    if "$RDIS_ROOT/cli/commands/process-review.sh" "${rdis_args[@]}"; then
        log_info "RDIS processing completed successfully"

        # Display status summary
        echo ""
        echo " Review-Driven Implementation System Results:"
