        echo " RDIS processing failed. Check logs for details:"
        "$RDIS_ROOT/cli/commands/error-report.sh" --session-id="$session_id" --recent
    fi
}

# Performance monitoring
monitor_hook_performance() {
    local hook_end=$(date +%s%N)
    local duration=$(( (hook_end - HOOK_START) / 1000000 )) # milliseconds

    log_debug "PostToolUse hook execution time: ${duration}ms"

    # Alert if hook is taking too long
    if (( duration > 5000 )); then
        log_error "Hook execution exceeded 5 seconds (${duration}ms)"
    fi
}

# Error handling
handle_rdis_error() {
    log_error "RDIS integration encountered an error: $1"
    echo "  RDIS processing failed, but Claude Code operation completed normally"
    echo "   Check RDIS logs for troubleshooting information"
}

# Main hook execution
main() {
