class TemplateGenerator {
  constructor(config = {}) {
    this.templateRegistry = new TemplateRegistry();
    this.contextAnalyzer = new ContextAnalyzer();
    this.variableExtractor = new VariableExtractor();
    this.customizer = new TemplateCustomizer();
    this.config = { ...this.defaultConfig, ...config };
  }

  async generateImplementationTemplate(recommendation, context) {
    try {
      // Select appropriate template
      const templateSpec = this.selectTemplate(recommendation);

      // Extract variables for template processing
      const variables = await this.extractVariables(recommendation, context);

      // Analyze customizations needed
      const customizations = this.analyzeCustomizations(context);

      // Load and process template
      const template = await this.templateRegistry.load(templateSpec.template_path);
      const customizedTemplate = await this.customizer.customize(template, customizations);
      const processedTemplate = await this.processTemplate(customizedTemplate, variables);

      return {
        template_content: processedTemplate,
        variables_used: variables,
        customizations_applied: customizations,
        implementation_guidance: this.generateGuidance(recommendation, templateSpec),
        verification_checklist: this.generateVerificationChecklist(recommendation)
      };
    } catch (error) {
      throw new TemplateGenerationError(`Failed to generate template: ${error.message}`, error);
    }
  }

  selectTemplate(recommendation) {
    const candidates = this.templateRegistry.findMatching(recommendation);
    const scored = this.scoreCandidates(candidates, recommendation);

    if (scored.length === 0) {
      throw new TemplateGenerationError('No suitable template found for recommendation');
    }

    return scored[0]; // Return highest scored template
  }

  async extractVariables(recommendation, context) {
    return {
      rdis: {
        recommendation: this.extractRecommendationVariables(recommendation),
        context: this.extractContextVariables(context),
        classification: recommendation.classification,
        metadata: {
          processing_timestamp: new Date().toISOString(),
          classifier_version: this.config.version,
          template_version: this.templateRegistry.getVersion()
        }
      },
      project: this.extractProjectVariables(context.project_info),
      technical: this.extractTechnicalVariables(context.technical_context)
    };
  }

  // Additional methods...
}

module.exports = TemplateGenerator;
```

