// Global test setup for RDIS testing suite
const path = require('path');
const fs = require('fs');

// Configure test environment
process.env.NODE_ENV = 'test';
process.env.RDIS_TEST_MODE = 'true';
process.env.RDIS_LOG_LEVEL = 'error'; // Suppress logs during testing

// Set up test directories
const testRoot = path.join(__dirname, '..');
const fixturesDir = path.join(testRoot, 'fixtures');
const reportsDir = path.join(testRoot, 'reports');

// Ensure test directories exist
[reportsDir, path.join(reportsDir, 'coverage'), path.join(reportsDir, 'performance'), path.join(reportsDir, 'security')].forEach(dir => {
  if (!fs.existsSync(dir)) {
    fs.mkdirSync(dir, { recursive: true });
  }
});

// Global test configuration
global.RDIS_TEST_CONFIG = {
  timeout: 30000,
  retries: 2,
  fixtures: fixturesDir,
  reports: reportsDir,
  tempDir: path.join(testRoot, 'temp'),
  mockData: path.join(fixturesDir, 'mock-data')
};

// Mock console methods for testing
const originalConsole = { ...console };
global.mockConsole = () => {
  console.log = jest.fn();
  console.error = jest.fn();
  console.warn = jest.fn();
  console.info = jest.fn();
  console.debug = jest.fn();
};

global.restoreConsole = () => {
  Object.assign(console, originalConsole);
};

// Test utilities
global.testUtils = {
  // Wait for a condition to be met
  waitFor: async (condition, timeout = 5000) => {
    const start = Date.now();
    while (Date.now() - start < timeout) {
      if (await condition()) {
        return true;
      }
      await new Promise(resolve => setTimeout(resolve, 100));
    }
    throw new Error(`Condition not met within ${timeout}ms`);
  },

  // Create temporary file
  createTempFile: (content, extension = '.js') => {
    const tempDir = global.RDIS_TEST_CONFIG.tempDir;
    if (!fs.existsSync(tempDir)) {
      fs.mkdirSync(tempDir, { recursive: true });
    }
    const filename = `test_${Date.now()}_${Math.random().toString(36).substr(2, 9)}${extension}`;
    const filepath = path.join(tempDir, filename);
    fs.writeFileSync(filepath, content);
    return filepath;
  },

  // Clean up temporary files
  cleanupTempFiles: () => {
    const tempDir = global.RDIS_TEST_CONFIG.tempDir;
    if (fs.existsSync(tempDir)) {
      fs.rmSync(tempDir, { recursive: true, force: true });
    }
  },

  // Load fixture data
  loadFixture: (name) => {
    const fixturePath = path.join(fixturesDir, name);
    if (!fs.existsSync(fixturePath)) {
      throw new Error(`Fixture not found: ${name}`);
    }
    const content = fs.readFileSync(fixturePath, 'utf8');
    return path.extname(fixturePath) === '.json' ? JSON.parse(content) : content;
  },

  // Generate test data
  generateTestReview: (options = {}) => {
    const defaults = {
      severity: 'medium',
      category: 'quality',
      file_path: 'src/test.js',
      recommendations: 1
    };
    const config = { ...defaults, ...options };

    return {
      content: `# Code Review Results\n\n## ${config.category} Issues\n\n### ${config.severity} Priority\n\n- Recommendation 1: ${config.file_path} needs improvement`,
      metadata: {
        reviewer: 'test-reviewer',
        timestamp: new Date().toISOString(),
        files_reviewed: [config.file_path],
        focus_areas: [config.category]
      }
    };
  },

  // Generate mock implementation
  generateMockImplementation: (type = 'auto') => {
    return {
      type,
      status: 'pending',
      changes: [],
      affected_files: ['src/test.js'],
      backup_id: 'test_backup_123',
      verification_results: null
    };
  }
};

// Test assertions helpers
global.assertions = {
  // Assert RDIS component structure
  assertRDISComponent: (component) => {
    expect(component).toBeDefined();
    expect(typeof component).toBe('object');
    expect(component.constructor).toBeDefined();
  },

  // Assert review parsing result
  assertParsedReview: (parsed) => {
    expect(parsed).toHaveProperty('recommendations');
    expect(parsed).toHaveProperty('metadata');
    expect(parsed).toHaveProperty('parsing_confidence');
    expect(parsed).toHaveProperty('errors');
    expect(Array.isArray(parsed.recommendations)).toBe(true);
    expect(typeof parsed.parsing_confidence).toBe('number');
  },

  // Assert classification result
  assertClassificationResult: (result) => {
    expect(result).toHaveProperty('implementation_type');
    expect(result).toHaveProperty('confidence');
    expect(result).toHaveProperty('reasoning');
    expect(['auto', 'template', 'manual']).toContain(result.implementation_type);
    expect(typeof result.confidence).toBe('number');
    expect(result.confidence).toBeGreaterThanOrEqual(0);
    expect(result.confidence).toBeLessThanOrEqual(1);
  },

  // Assert verification result
  assertVerificationResult: (result) => {
    expect(result).toHaveProperty('status');
    expect(['passed', 'failed', 'warning']).toContain(result.status);
    if (result.status === 'failed') {
      expect(result).toHaveProperty('errors');
      expect(Array.isArray(result.errors)).toBe(true);
    }
  }
};

// Mock factory for RDIS components
global.mockFactory = {
  createMockReviewParser: () => ({
    parse: jest.fn().mockResolvedValue({
      recommendations: [],
      metadata: {},
      parsing_confidence: 0.9,
      errors: []
    })
  }),

  createMockClassifier: () => ({
    classify: jest.fn().mockResolvedValue({
      implementation_type: 'auto',
      confidence: 0.8,
      reasoning: 'Test classification',
      estimated_effort: 'low'
    })
  }),

  createMockAutoFixEngine: () => ({
    executeAutoFix: jest.fn().mockResolvedValue({
      status: 'success',
      changes_applied: [],
      validation_results: { valid: true },
      backup_id: 'test_backup'
    })
  }),

  createMockTemplateGenerator: () => ({
    generateImplementationTemplate: jest.fn().mockResolvedValue({
      template_content: 'Test template content',
      variables_used: {},
      customizations_applied: [],
      implementation_guidance: 'Test guidance'
    })
  }),

  createMockVerificationEngine: () => ({
    executeVerification: jest.fn().mockResolvedValue({
      overall_status: 'passed',
      verification_results: [],
      performance_metrics: {},
      security_status: 'secure'
    })
  })
};

// Setup and teardown hooks
beforeEach(() => {
  // Reset mocks
  jest.clearAllMocks();

  // Clean up temp files from previous tests
  try {
    global.testUtils.cleanupTempFiles();
