#!/usr/bin/env node
/* Wrapper templater placed at scripts/render-readme.js (keeps existing documentation references intact). */
import fs from 'fs';
import path from 'path';
import { fileURLToPath } from 'url';

import dotenv from 'dotenv';

let renderTemplate;
try {
  ({ renderTemplate } = await import('../packages/template/src/index.js'));
} catch {
  renderTemplate = (tpl, env) => tpl.replace(/\{\{(.*?)\}\}/g, (_, k) => env[k] || '');
}

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);
const root = path.resolve(__dirname, '..');
const envPath = path.join(root, '.env');
const envLocalPath = path.join(root, '.env.local');
const templatePath = path.join(root, 'README.template.md');
const outputPath = path.join(root, 'README.md');

if (fs.existsSync(envPath)) {
  dotenv.config({ path: envPath });
  console.log('[render-readme] loaded .env');
} else if (fs.existsSync(envLocalPath)) {
  dotenv.config({ path: envLocalPath });
  console.log('[render-readme] loaded .env.local');
} else {
  console.log('[render-readme] no .env* found');
}

try {
  const tpl = fs.readFileSync(templatePath, 'utf8');
  const missing = new Set();
  const rendered = renderTemplate(tpl, process.env, (k) => missing.add(k));
  fs.writeFileSync(outputPath, rendered, 'utf8');
  console.log(
    '[render-readme] wrote README.md' +
      (missing.size ? ` (missing: ${[...missing].join(', ')})` : '')
  );
} catch (err) {
  console.error('[render-readme] failed', err);
  process.exit(1);
}
