# Input sanitization for AI systems
import re
from typing import List, Dict

class AIInputSanitizer:
    def __init__(self):
        self.dangerous_patterns = [
            r'<script.*?</script>',  # Script injection
            r'javascript:',          # JavaScript execution
            r'eval\(',              # Code evaluation
            r'exec\(',              # Code execution
            r'\|\||&&',             # Command injection
        ]

    def sanitize(self, user_input: str) -> str:
        sanitized = user_input

        # Remove dangerous patterns
        for pattern in self.dangerous_patterns:
            sanitized = re.sub(pattern, '', sanitized, flags=re.IGNORECASE)

        # Limit input length
        sanitized = sanitized[:1000]

        # Encode special characters
        sanitized = sanitized.encode('ascii', 'ignore').decode('ascii')

        return sanitized

    def validate_ai_prompt(self, prompt: str) -> bool:
        """Validate AI prompt for injection attempts"""
        suspicious_keywords = [
            'ignore previous instructions',
            'forget your role',
            'system prompt:',
            'you are now',
            'pretend to be'
        ]

        prompt_lower = prompt.lower()
        return not any(keyword in prompt_lower for keyword in suspicious_keywords)

# Usage in AI system
class SecureAIAgent:
    def __init__(self):
        self.sanitizer = AIInputSanitizer()

    def process_request(self, user_input: str) -> str:
        # Sanitize input
        clean_input = self.sanitizer.sanitize(user_input)

        # Validate prompt
        if not self.sanitizer.validate_ai_prompt(clean_input):
            raise SecurityError("Suspicious prompt detected")

        # Process with clean input
        return self.ai_model.generate_response(clean_input)
