from sklearn.ensemble import IsolationForest
import numpy as np

class AIThreatDetector:
    def __init__(self):
        self.anomaly_detector = IsolationForest(contamination=0.1)
        self.prompt_analyzer = PromptAnalyzer()
        self.behavior_monitor = BehaviorMonitor()

    def analyze_request(self, request: dict) -> ThreatAssessment:
        features = self.extract_features(request)

        # Anomaly detection
        anomaly_score = self.anomaly_detector.decision_function([features])[0]

        # Prompt analysis
        prompt_risk = self.prompt_analyzer.assess_risk(request['prompt'])

        # Behavioral analysis
        behavior_risk = self.behavior_monitor.assess_user_behavior(request['user_id'])

        # Combine scores
        overall_risk = self.combine_risk_scores(anomaly_score, prompt_risk, behavior_risk)

        return ThreatAssessment(
            risk_level=self.categorize_risk(overall_risk),
            confidence=self.calculate_confidence(overall_risk),
            recommended_action=self.recommend_action(overall_risk),
            details={'anomaly': anomaly_score, 'prompt': prompt_risk, 'behavior': behavior_risk}
        )

    def extract_features(self, request: dict) -> np.array:
