      fs.readFileSync(file, 'utf8').includes('infrastructure')
    );
    expect(violations).toEqual([]);
  });

  it('should sanitize all inputs', () => {
    const userInput = '<script>alert("xss")</script>';
    const sanitized = InputSanitizer.sanitizeForAI(userInput);
    expect(sanitized).not.toContain('<script>');
  });

  it('should provide AI-friendly responses', () => {
    const response = new ApiResponse({ id: 1 }, true);
    expect(response.success).toBe(true);
    expect(response.data).toBeDefined();
  });
});
```

