const licenseKey = process.env.WORKSPACE_LICENSE || '';
const ownerName = process.env.OWNER_NAME || '';

try {
  const tpl = fs.readFileSync(tplPath, 'utf8');
  let out = tpl.replace(/\{\{\s*WORKSPACE_LICENSE\s*\}\}/g, licenseKey);
  out = out.replace(/\{\{\s*OWNER_NAME\s*\}\}/g, ownerName);
  fs.writeFileSync(outPath, out, 'utf8');
  console.log(`Rendered LICENSE.md with license: ${licenseKey || '(missing)'} and owner: ${ownerName || '(missing)'}`);
} catch (e) {
  console.error(e);
  process.exit(1);
}

