#!/usr/bin/env node
import fs from 'fs/promises';
import path from 'path';
import { fileURLToPath } from 'url';
import dotenv from 'dotenv';
import { renderTemplate } from '../packages/template/src/index.js';

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);
const root = path.resolve(__dirname, '..');

// Template mappings
const templateMappings = [
  {
    src: '.devcontainer/devcontainer.json.template',
    dest: '.devcontainer/devcontainer.json'
  },
  {
    src: '.devcontainer/Dockerfile.template', 
    dest: '.devcontainer/Dockerfile'
  },
  {
    src: '.devcontainer/docker-compose.yml.template',
    dest: '.devcontainer/docker-compose.yml'
  }
];

// Load environment variables
function loadEnv() {
  const envPath = path.join(root, '.env');
  const envLocalPath = path.join(root, '.env.local');

  try {
    if (await fs.access(envPath).then(() => true).catch(() => false)) {
      dotenv.config({ path: envPath });
      console.log('Loaded environment from .env');
    } else if (await fs.access(envLocalPath).then(() => true).catch(() => false)) {
      dotenv.config({ path: envLocalPath });
      console.log('Loaded environment from .env.local');
    } else {
      console.warn('No .env or .env.local found; using process.env only');
    }
  } catch (error) {
    console.error('Error loading environment:', error);
  }
}

// Render template file
async function renderTemplateFile(srcPath, destPath, env) {
  try {
    const absSrc = path.resolve(root, srcPath);
    const absDest = path.resolve(root, destPath);

    const templateContent = await fs.readFile(absSrc, 'utf-8');
    const renderedContent = renderTemplate(templateContent, env, (missingVar) => {
      console.warn(`Warning: Missing variable ${missingVar} in ${srcPath}`);
    });

    // Ensure destination directory exists
    await fs.mkdir(path.dirname(absDest), { recursive: true });

    // Write rendered content
    await fs.writeFile(absDest, renderedContent, 'utf-8');

    console.log(`✅ Generated ${destPath}`);
    return true;
  } catch (error) {
    console.error(`❌ Error generating ${destPath}:`, error);
    return false;
  }
}

// Main execution
async function main() {
  console.log('🚀 Generating DevContainer configuration...');

  await loadEnv();

  let allSuccessful = true;

  for (const mapping of templateMappings) {
    const success = await renderTemplateFile(mapping.src, mapping.dest, process.env);
    allSuccessful = allSuccessful && success;
  }

  if (allSuccessful) {
    console.log('\n🎉 DevContainer generation completed successfully!');
    console.log('\n📋 Next steps:');
    console.log('1. Review generated files in .devcontainer/');
    console.log('2. Open VS Code and run "Reopen in Container"');
    console.log('3. Test with: npm test');
  } else {
    console.error('\n❌ DevContainer generation failed. Check errors above.');
    process.exit(1);
  }
}

// Run main function
main().catch((error) => {
  console.error('Fatal error:', error);
  process.exit(1);
});

