import { describe, it, expect, beforeEach, afterEach } from 'vitest';
import { renderTemplate, extractTokens } from '../packages/template/src/index.js';
import fs from 'fs/promises';
import path from 'path';

describe('DevContainer Template Generation', () => {
  const mockEnv = {
    WORKSPACE_NAME: 'test-workspace',
    NODE_VERSION: '20',
    PYTHON_VERSION: '3.11'
  };

  describe('Template Token Extraction', () => {
    it('should extract all tokens from devcontainer.json template', async () => {
      const templatePath = path.resolve('.devcontainer/devcontainer.json.template');
      const templateContent = await fs.readFile(templatePath, 'utf-8');
      const tokens = extractTokens(templateContent);

      expect(tokens).toContain('WORKSPACE_NAME');
      expect(tokens).toContain('NODE_VERSION');
      expect(tokens).toContain('PYTHON_VERSION');
    });

    it('should extract tokens from Dockerfile template', async () => {
      const templatePath = path.resolve('.devcontainer/Dockerfile.template');
      const templateContent = await fs.readFile(templatePath, 'utf-8');
      const tokens = extractTokens(templateContent);

      expect(tokens).toContain('NODE_VERSION');
      expect(tokens).toContain('PYTHON_VERSION');
    });

    it('should extract tokens from docker-compose template', async () => {
      const templatePath = path.resolve('.devcontainer/docker-compose.yml.template');
      const templateContent = await fs.readFile(templatePath, 'utf-8');
      const tokens = extractTokens(templateContent);

      expect(tokens).toContain('WORKSPACE_NAME');
    });
  });

  describe('Template Rendering', () => {
    it('should render devcontainer.json without leftover tokens', async () => {
      const templatePath = path.resolve('.devcontainer/devcontainer.json.template');
      const templateContent = await fs.readFile(templatePath, 'utf-8');

      const rendered = renderTemplate(templateContent, mockEnv);

      // Should not contain any unresolved tokens
      expect(rendered).not.toMatch(/\{\{.*\}\}/);

      // Should contain resolved values
      expect(rendered).toContain('test-workspace-devcontainer');
      expect(rendered).toContain('javascript-node:20');
      expect(rendered).toContain('"version": "3.11"');
    });

    it('should render Dockerfile without leftover tokens', async () => {
      const templatePath = path.resolve('.devcontainer/Dockerfile.template');
      const templateContent = await fs.readFile(templatePath, 'utf-8');

      const rendered = renderTemplate(templateContent, mockEnv);

      // Should not contain any unresolved tokens
      expect(rendered).not.toMatch(/\{\{.*\}\}/);

      // Should contain resolved values
      expect(rendered).toContain('javascript-node:20');
      expect(rendered).toContain('PYTHON_VERSION=3.11');
    });

    it('should render docker-compose.yml without leftover tokens', async () => {
      const templatePath = path.resolve('.devcontainer/docker-compose.yml.template');
      const templateContent = await fs.readFile(templatePath, 'utf-8');

      const rendered = renderTemplate(templateContent, mockEnv);

      // Should not contain any unresolved tokens
      expect(rendered).not.toMatch(/\{\{.*\}\}/);

      // Should contain resolved values
      expect(rendered).toContain('test-workspace_dev');
    });

    it('should handle missing variables gracefully', () => {
      const template = '{"name": "{{WORKSPACE_NAME}}", "missing": "{{MISSING_VAR}}"}';
      const partialEnv = { WORKSPACE_NAME: 'test' };

      const rendered = renderTemplate(template, partialEnv);

      // Should resolve known variables and leave empty string for missing
      expect(rendered).toBe('{"name": "test", "missing": ""}');
    });
  });

  describe('Generated JSON Validity', () => {
    it('should generate valid JSON for devcontainer.json', async () => {
      const templatePath = path.resolve('.devcontainer/devcontainer.json.template');
      const templateContent = await fs.readFile(templatePath, 'utf-8');

      const rendered = renderTemplate(templateContent, mockEnv);

      // Should parse as valid JSON
      expect(() => JSON.parse(rendered)).not.toThrow();

      const parsed = JSON.parse(rendered);
      expect(parsed.name).toBe('test-workspace-devcontainer');
      expect(parsed.image).toBe('mcr.microsoft.com/devcontainers/javascript-node:20');
    });
  });

  describe('Generated YAML Validity', () => {
    it('should generate valid YAML structure for docker-compose', async () => {
      const templatePath = path.resolve('.devcontainer/docker-compose.yml.template');
      const templateContent = await fs.readFile(templatePath, 'utf-8');

      const rendered = renderTemplate(templateContent, mockEnv);

      // Basic YAML structure validation (no YAML parser in test, but check structure)
      expect(rendered).toContain('version:');
      expect(rendered).toContain('services:');
      expect(rendered).toContain('volumes:');
      expect(rendered).not.toMatch(/\{\{.*\}\}/);
    });
  });
});

