import requests
import json

def assess_api_ai_readiness(endpoint):
    """Check if API is suitable for AI agent consumption"""
    response = requests.get(f"{endpoint}/api/schema")

    checklist = {
        'has_openapi_spec': 'openapi' in response.json(),
        'structured_responses': all(isinstance(v, dict) for v in response.json().values()),
        'error_handling': 'error' in response.json(),
        'rate_limiting': 'x-rate-limit' in response.headers
