// src/application/services/InputSanitizer.ts
export class InputSanitizer {
  static sanitizeForAI(input: string): string {
    // Remove potential injection attempts
    const dangerous = [
      /system\s*prompt:/gi,
      /ignore\s*previous\s*instructions/gi,
      /you\s*are\s*now/gi,
      /<script.*?>/gi,
