input.new_string:
"hooks": {
  "PreCommit": [
    {
      "type": "command",
      "command": "npm run review",
      "timeout": 120,
      "description": "Quality gate: run full review pipeline before commit"
    }
  ],
  "PreFileWrite": [
    {
      "matcher": ".*README\\.md$",
      "hooks": [
        {
          "type": "command",
          "command": "echo 'ERROR: Edit README.template.md instead of README.md' && exit 1",
          "description": "Enforce template-first methodology for README"
        }
      ]
    },
    {
      "matcher": ".*LICENSE\\.md$",
      "hooks": [
        {
          "type": "command",
          "command": "echo 'ERROR: Use npm run render:license instead' && exit 1",
          "description": "Enforce template-first methodology for LICENSE"
        }
      ]
    }
  ],
  "UserPromptSubmitHook": [
    {
      "type": "command",
      "command": "echo 'üìã Session hygiene check: Have you read mission-charter.md and current-sequence.md?'",
      "timeout": 5,
      "description": "Remind about mandatory governance files"
    }
  ],
  "PostError": [
    {
      "type": "command",
      "command": "echo 'üîç Error occurred. Remember to log in global CLAUDE.md: Root cause ‚Üí Prevention protocol'",
      "timeout": 5,
      "description": "Promote systematic error logging"
    }
  ],
  "PreBash": [
    {
      "matcher": ".*(rm -rf|sudo|--force|del /f|rmdir /s).*",
      "hooks": [
        {
          "type": "command",
          "command": "echo '‚ö†Ô∏è  Dangerous command detected. Type YES to confirm: ' && read -r confirm && [[ \"$confirm\" != \"YES\" ]] && exit 1",
          "timeout": 30,
          "description": "Prevent accidental destructive operations"
        }
      ]
    }
  ],
  "PostToolUse": [
    {
      "matcher": ".*write_file.*|.*edit_file.*",
      "hooks": [
        {
          "type": "command",
          "command": "npm run format",
          "timeout": 30,
          "description": "Auto-format after file modifications"
        }
      ]
    },
    {
      "matcher": ".*Edit.*|.*MultiEdit.*",
      "hooks": [
        {
          "type": "command",
          "command": "echo 'üí° File modified. Consider running /review to validate changes.'",
          "timeout": 5,
          "description": "Promote quality verification after edits"
        }
      ]
    }
  ],
  "PreSessionStart": [
    {
      "type": "command",
      "command": "git status --porcelain 2>/dev/null | wc -l | xargs -I {} echo 'üìä Workspace status: {} uncommitted changes'",
      "timeout": 10,
      "description": "Workspace awareness for 'one thread only' principle"
    }
  ],
  "PostCommit": [
    {
      "type": "command",
      "command": "npm run render:readme > /dev/null 2>&1 || echo 'üìù Note: README auto-regeneration attempted'",
      "timeout": 30,
      "description": "Auto-regenerate documentation after commits"
    }
  ]
}

