#!/usr/bin/env node

/**
 * Validate .claude/ Components Against Schema
 * Integrates with npm run review pipeline
 */

import fs from 'fs/promises';
import path from 'path';
import Ajv from 'ajv';
import addFormats from 'ajv-formats';
import yaml from 'js-yaml';

const SCHEMA_PATH = '.claude/templates/schema.json';
const COMPONENTS_DIRS = [
  '.claude/commands',
  '.claude/agents', 
  '.claude/workflows'
];

async function loadSchema() {
  try {
    const schemaContent = await fs.readFile(SCHEMA_PATH, 'utf-8');
    return JSON.parse(schemaContent);
  } catch (error) {
    console.error(`‚ùå Failed to load schema: ${error.message}`);
    process.exit(1);
  }
}

async function extractMetadata(filePath) {
  const content = await fs.readFile(filePath, 'utf-8');

  // Extract YAML frontmatter
  const frontmatterMatch = content.match(/^---\n(.*?)\n---/s);
  if (!frontmatterMatch) {
    throw new Error(`No YAML frontmatter found in ${filePath}`);
  }

  return yaml.load(frontmatterMatch[1]);
}

async function validateComponents() {
  console.log('üîç Validating .claude/ components against schema...\n');

  const schema = await loadSchema();
  const ajv = new Ajv();
  addFormats(ajv);

  let totalFiles = 0;
  let validFiles = 0;
  let errors = [];

  for (const dir of COMPONENTS_DIRS) {
    try {
      const files = await fs.readdir(dir);
      const mdFiles = files.filter(f => f.endsWith('.md') && f !== 'README.md');

      console.log(`üìÅ Validating ${dir}/ (${mdFiles.length} files)`);

      for (const file of mdFiles) {
        totalFiles++;
        const filePath = path.join(dir, file);

        try {
          const metadata = await extractMetadata(filePath);

          // Determine component type and validate
          let componentSchema;
          if (dir.includes('commands')) {
            componentSchema = schema.definitions.command;
          } else if (dir.includes('agents')) {
            componentSchema = schema.definitions.agent;
          } else {
            componentSchema = schema.definitions.metadata;
          }

          const validate = ajv.compile(componentSchema);
          const valid = validate(metadata);

          if (valid) {
            console.log(`  ‚úÖ ${file}`);
            validFiles++;
          } else {
            console.log(`  ‚ùå ${file}`);
            errors.push({
              file: filePath,
              errors: validate.errors
            });
          }

        } catch (error) {
          console.log(`  ‚ùå ${file} - ${error.message}`);
          errors.push({
            file: filePath,
            errors: [error.message]
          });
        }
      }

      console.log('');
    } catch (error) {
      if (error.code !== 'ENOENT') {
        console.error(`‚ùå Error reading ${dir}: ${error.message}`);
      }
    }
  }

  // Summary
  console.log('üìä Validation Summary');
  console.log(`   Total files: ${totalFiles}`);
  console.log(`   Valid files: ${validFiles}`);
  console.log(`   Invalid files: ${totalFiles - validFiles}`);

  if (errors.length > 0) {
    console.log('\nüîç Validation Errors:');
    errors.forEach(({ file, errors: fileErrors }) => {
      console.log(`\n${file}:`);
      fileErrors.forEach(error => {
        console.log(`  ‚Ä¢ ${error.instancePath || 'root'}: ${error.message || error}`);
      });
    });

    console.log('\nüí° Fix errors or run: npm run claude:migrate');
    process.exit(1);
  }

  console.log('\n‚úÖ All .claude/ components are valid!');
}

validateComponents().catch(console.error);

