#!/usr/bin/env node

/**
 * {{COMPONENT_TYPE}} Generator
 * Generated from template: generator-script.js.tpl v{{TEMPLATE_VERSION}}
 * 
 * Usage: node {{SCRIPT_NAME}} <{{PARAMETER_NAME}}>
 */

import fs from 'fs/promises';
import path from 'path';

// Configuration
const TEMPLATE_PATH = '.claude/templates/{{TEMPLATE_FILE}}';
const OUTPUT_DIR = '.claude/{{OUTPUT_DIRECTORY}}';
const COMPONENT_TYPE = '{{COMPONENT_TYPE}}';

/**
 * Generate {{COMPONENT_TYPE}} from template
 */
async function generate{{COMPONENT_TYPE_PASCAL}}({{PARAMETER_NAME}}, options = {}) {
  const {
    author = 'Omar El Mountassir',
    status = 'active',
    platform = 'cross-platform',
    gate = 'C',
    {{#each DEFAULT_OPTIONS}}
    {{name}} = {{value}},
    {{/each}}
  } = options;

  const date = new Date().toISOString().split('T')[0];
  const version = '1.0.0';

  try {
    // Validate input
    if (!{{PARAMETER_NAME}} || {{PARAMETER_NAME}}.length === 0) {
      throw new Error('{{PARAMETER_NAME}} is required and cannot be empty');
    }

    // Read template
    const template = await fs.readFile(TEMPLATE_PATH, 'utf-8');

    // Replace placeholders
    const content = template
      .replace(/\{\{VERSION\}\}/g, version)
      .replace(/\{\{AUTHOR\}\}/g, author)
      .replace(/\{\{DATE\}\}/g, date)
      .replace(/\{\{STATUS\}\}/g, status)
      .replace(/\{\{PLATFORM\}\}/g, platform)
      .replace(/\{\{GATE\}\}/g, gate)
      .replace(/\{\{{{PLACEHOLDER_KEY}}\}\}/g, {{PARAMETER_NAME}})
      {{#each REPLACEMENTS}}
      .replace(/\{\{{{key}}\}\}/g, {{value}})
      {{/each}};

    // Ensure output directory exists
    await fs.mkdir(OUTPUT_DIR, { recursive: true });

    // Write generated component
    const outputPath = path.join(OUTPUT_DIR, `${{{PARAMETER_NAME}}}{{FILE_EXTENSION}}`);

    // Check if file already exists
    try {
      await fs.access(outputPath);
      console.log(`‚ö†Ô∏è  File already exists: ${outputPath}`);
      console.log(`   Use --force to overwrite or choose a different name`);
      return;
    } catch {
      // File doesn't exist, proceed
    }

    await fs.writeFile(outputPath, content);

    console.log(`‚úÖ Generated ${COMPONENT_TYPE}: ${outputPath}`);
    console.log(`üìù Edit the file to complete the template placeholders`);
    console.log(`üîç Validate with: npm run claude:validate`);

    // Post-generation instructions
    console.log(`\nüìã Next steps:`);
    console.log(`   1. Review and customize: ${outputPath}`);
    console.log(`   2. Run validation: npm run claude:validate`);
    console.log(`   3. Test functionality: {{TEST_COMMAND}}`);

  } catch (error) {
    console.error(`‚ùå Error generating ${COMPONENT_TYPE}: ${error.message}`);
    process.exit(1);
  }
}

/**
 * CLI Interface
 */
function showUsage() {
  console.log(`Usage: node ${path.basename(process.argv[1])} <{{PARAMETER_NAME}}> [options]`);
  console.log(`\nExample:`);
  console.log(`  node ${path.basename(process.argv[1])} {{EXAMPLE_VALUE}}`);
  console.log(`\nOptions:`);
  console.log(`  --author <name>     Author name (default: Omar El Mountassir)`);
  console.log(`  --status <status>   Status (active|deprecated|experimental)`);
  console.log(`  --platform <plat>   Platform (windows|linux|cross-platform)`);
  console.log(`  --gate <gate>       Governance gate (A|B|C)`);
  console.log(`  --force            Overwrite existing files`);
}

// Parse CLI arguments
const args = process.argv.slice(2);
const {{PARAMETER_NAME}} = args[0];

if (!{{PARAMETER_NAME}} || {{PARAMETER_NAME}} === '--help' || {{PARAMETER_NAME}} === '-h') {
  showUsage();
  process.exit({{PARAMETER_NAME}} ? 0 : 1);
}

// Parse options
const options = {};
for (let i = 1; i < args.length; i += 2) {
  const key = args[i]?.replace('--', '');
  const value = args[i + 1];
  if (key && value) {
    options[key] = value;
  }
}

generate{{COMPONENT_TYPE_PASCAL}}({{PARAMETER_NAME}}, options);

