#!/usr/bin/env node

/**
 * {{MIGRATION_TYPE}} Migration Script
 * Generated from template: migration-script.js.tpl v{{TEMPLATE_VERSION}}
 * 
 * Purpose: {{PURPOSE}}
 * Usage: node {{SCRIPT_NAME}} [--dry-run] [--force]
 */

import fs from 'fs/promises';
import path from 'path';
import yaml from 'js-yaml';

// Configuration
const SOURCE_DIR = '{{SOURCE_DIRECTORY}}';
const TARGET_DIR = '{{TARGET_DIRECTORY}}';
const BACKUP_DIR = '{{BACKUP_DIRECTORY}}';
const TEMPLATE_PATH = '{{TEMPLATE_PATH}}';

/**
 * Migration Configuration
 */
const MIGRATION_CONFIG = {
  name: '{{MIGRATION_NAME}}',
  version: '{{VERSION}}',
  created_by: '{{AUTHOR}}',
  created_date: '{{DATE}}',
  description: '{{DESCRIPTION}}',
  governance_gate: '{{GATE}}',
  breaking_changes: {{BREAKING_CHANGES}},
  rollback_supported: {{ROLLBACK_SUPPORTED}}
};

/**
 * Backup existing files before migration
 */
async function createBackup(files) {
  const timestamp = new Date().toISOString().replace(/[:.]/g, '-');
  const backupPath = path.join(BACKUP_DIR, `backup-${timestamp}`);

  console.log(`üì¶ Creating backup at: ${backupPath}`);

  await fs.mkdir(backupPath, { recursive: true });

  for (const file of files) {
    const sourcePath = file.path;
    const relativePath = path.relative(process.cwd(), sourcePath);
    const targetPath = path.join(backupPath, relativePath);

    // Create directory structure
    await fs.mkdir(path.dirname(targetPath), { recursive: true });

    // Copy file
    await fs.copyFile(sourcePath, targetPath);
    console.log(`   ‚úÖ Backed up: ${relativePath}`);
  }

  // Create backup manifest
  const manifest = {
    backup_date: new Date().toISOString(),
    migration: MIGRATION_CONFIG,
    files: files.map(f => ({ path: f.path, type: f.type, size: f.size }))
  };

  await fs.writeFile(
    path.join(backupPath, 'backup-manifest.json'),
    JSON.stringify(manifest, null, 2)
  );

  return backupPath;
}

/**
 * Analyze existing files for migration
 */
async function analyzeFiles() {
  const files = [];

  try {
    const entries = await fs.readdir(SOURCE_DIR, { withFileTypes: true });

    for (const entry of entries) {
      if (entry.isFile() && {{FILE_FILTER}}) {
        const filePath = path.join(SOURCE_DIR, entry.name);
        const content = await fs.readFile(filePath, 'utf-8');
        const stats = await fs.stat(filePath);

        // Analyze file structure
        const analysis = await analyzeFileStructure(filePath, content);

        files.push({
          path: filePath,
          name: entry.name,
          type: analysis.type,
          size: stats.size,
          hasMetadata: analysis.hasMetadata,
          needsMigration: analysis.needsMigration,
          complexity: analysis.complexity,
          content: content
        });
      }
    }

  } catch (error) {
    if (error.code !== 'ENOENT') {
      throw error;
    }
  }

  return files;
}

/**
 * Analyze individual file structure
 */
async function analyzeFileStructure(filePath, content) {
  const analysis = {
    type: 'unknown',
    hasMetadata: false,
    needsMigration: false,
    complexity: 'simple',
    issues: []
  };

  // Detect file type
  if (filePath.endsWith('.md')) {
    analysis.type = detectMarkdownType(content);
  } else if (filePath.endsWith('.json')) {
    analysis.type = detectJsonType(content);
  }

  // Check for existing metadata
  const frontmatterMatch = content.match(/^---\n(.*?)\n---/s);
  if (frontmatterMatch) {
    try {
      const metadata = yaml.load(frontmatterMatch[1]);
      analysis.hasMetadata = true;

      // Check if metadata follows current schema
      if (!metadata.version || !metadata.created_by || !metadata.type) {
        analysis.needsMigration = true;
        analysis.issues.push('Incomplete metadata schema');
      }
    } catch (error) {
      analysis.issues.push('Invalid YAML frontmatter');
      analysis.needsMigration = true;
    }
  } else {
    analysis.needsMigration = true;
    analysis.issues.push('No metadata frontmatter');
  }

  return analysis;
}

/**
 * {{MIGRATION_LOGIC_FUNCTION}}
 */
async function {{MIGRATION_FUNCTION_NAME}}(file, options = {}) {
  console.log(`üîÑ Migrating: ${file.name}`);

  const { dryRun = false } = options;

  try {
    // Read template
    const template = await fs.readFile(TEMPLATE_PATH, 'utf-8');

    // Extract existing content and migrate to new format
    const migratedContent = await {{CONTENT_MIGRATION_FUNCTION}}(file, template);

    if (dryRun) {
      console.log(`   üìã [DRY RUN] Would migrate to new format`);
      return { success: true, action: 'dry-run' };
    }

    // Write migrated file
    const targetPath = path.join(TARGET_DIR, file.name);
    await fs.writeFile(targetPath, migratedContent);

    console.log(`   ‚úÖ Migrated successfully`);

    return { success: true, action: 'migrated', targetPath };

  } catch (error) {
    console.log(`   ‚ùå Migration failed: ${error.message}`);
    return { success: false, error: error.message };
  }
}

/**
 * Content migration logic (customize per migration type)
 */
async function {{CONTENT_MIGRATION_FUNCTION}}(file, template) {
  // Extract metadata from existing content
  const metadata = extractExistingMetadata(file.content);

  // Generate new metadata following schema
  const newMetadata = {
    version: '1.0.0',
    created_by: metadata.author || 'Omar El Mountassir',
    created_date: new Date().toISOString().split('T')[0],
    modified_date: new Date().toISOString().split('T')[0],
    type: file.type,
    status: metadata.status || 'active',
    platform: 'cross-platform',
    governance_gate: 'C', // Default, should be reviewed
    ...metadata
  };

  // Replace template placeholders
  let migratedContent = template;

  // Replace metadata placeholders
  Object.keys(newMetadata).forEach(key => {
    const placeholder = `{{${key.toUpperCase()}}}`;
    migratedContent = migratedContent.replace(new RegExp(placeholder, 'g'), newMetadata[key]);
  });

  // Migrate content-specific placeholders
  {{CONTENT_SPECIFIC_MIGRATION}}

  return migratedContent;
}

/**
 * Extract existing metadata from file
 */
function extractExistingMetadata(content) {
  const metadata = {};

  // Try to extract from YAML frontmatter
  const frontmatterMatch = content.match(/^---\n(.*?)\n---/s);
  if (frontmatterMatch) {
    try {
      Object.assign(metadata, yaml.load(frontmatterMatch[1]));
    } catch (error) {
      console.warn('Failed to parse existing frontmatter:', error.message);
    }
  }

  // Extract from content patterns if no frontmatter
  {{METADATA_EXTRACTION_LOGIC}}

  return metadata;
}

/**
 * Main migration execution
 */
async function runMigration(options = {}) {
  const { dryRun = false, force = false } = options;

  console.log(`üöÄ Starting {{MIGRATION_NAME}} Migration`);
  console.log(`   Source: ${SOURCE_DIR}`);
  console.log(`   Target: ${TARGET_DIR}`);
  console.log(`   Mode: ${dryRun ? 'DRY RUN' : 'LIVE'}`);

  try {
    // Analyze existing files
    const files = await analyzeFiles();

    if (files.length === 0) {
      console.log('üìù No files found to migrate');
      return;
    }

    console.log(`üìä Found ${files.length} files for analysis:`);
    files.forEach(file => {
      const status = file.needsMigration ? '‚ö†Ô∏è  NEEDS MIGRATION' : '‚úÖ UP TO DATE';
      console.log(`   ${status} - ${file.name} (${file.type})`);
    });

    const filesToMigrate = files.filter(f => f.needsMigration);

    if (filesToMigrate.length === 0) {
      console.log('‚úÖ All files are already up to date!');
      return;
    }

    if (!force && !dryRun) {
      console.log(`\n‚ö†Ô∏è  This will migrate ${filesToMigrate.length} files.`);
      console.log('   Run with --dry-run to preview changes');
      console.log('   Run with --force to proceed');
      return;
    }

    // Create backup before migration
    let backupPath;
    if (!dryRun) {
      backupPath = await createBackup(filesToMigrate);
    }

    // Ensure target directory exists
    await fs.mkdir(TARGET_DIR, { recursive: true });

    // Migrate files
    const results = [];
    for (const file of filesToMigrate) {
      const result = await {{MIGRATION_FUNCTION_NAME}}(file, { dryRun });
      results.push(result);
    }

    // Summary
    const successful = results.filter(r => r.success).length;
    const failed = results.filter(r => !r.success).length;

    console.log(`\nüìà Migration Summary:`);
    console.log(`   ‚úÖ Successful: ${successful}`);
    console.log(`   ‚ùå Failed: ${failed}`);

    if (backupPath) {
      console.log(`   üì¶ Backup: ${backupPath}`);
    }

    if (!dryRun && successful > 0) {
      console.log(`\n‚úÖ Migration completed successfully!`);
      console.log(`üí° Next steps:`);
      console.log(`   1. Validate: npm run claude:validate`);
      console.log(`   2. Review changes and test functionality`);
      console.log(`   3. Commit changes with conventional commit message`);
    }

  } catch (error) {
    console.error(`‚ùå Migration failed: ${error.message}`);
    process.exit(1);
  }
}

/**
 * CLI Interface
 */
function showUsage() {
  console.log(`Usage: node ${path.basename(process.argv[1])} [options]`);
  console.log(`\nOptions:`);
  console.log(`  --dry-run    Preview changes without modifying files`);
  console.log(`  --force      Proceed with migration (required for live mode)`);
  console.log(`  --help       Show this help message`);
  console.log(`\nExample:`);
  console.log(`  node ${path.basename(process.argv[1])} --dry-run`);
  console.log(`  node ${path.basename(process.argv[1])} --force`);
}

// Parse CLI arguments
const args = process.argv.slice(2);
const options = {
  dryRun: args.includes('--dry-run'),
  force: args.includes('--force'),
  help: args.includes('--help')
};

if (options.help) {
  showUsage();
  process.exit(0);
}

runMigration(options);

#!/usr/bin/env node

/**
 * {{VALIDATION_TYPE}} Validation Script
 * Generated from template: validation-script.js.tpl v{{TEMPLATE_VERSION}}
 * 
 * Purpose: {{PURPOSE}}
 * Usage: node {{SCRIPT_NAME}} [--fix] [--strict] [--format json|text]
 */

import fs from 'fs/promises';
import path from 'path';
import Ajv from 'ajv';
import addFormats from 'ajv-formats';
import yaml from 'js-yaml';

// Configuration
const VALIDATION_CONFIG = {
  name: '{{VALIDATION_NAME}}',
  version: '{{VERSION}}',
  created_by: '{{AUTHOR}}',
  created_date: '{{DATE}}',
  description: '{{DESCRIPTION}}',
  governance_gate: '{{GATE}}',
  schema_path: '{{SCHEMA_PATH}}',
  target_directories: [{{TARGET_DIRECTORIES}}],
  file_patterns: [{{FILE_PATTERNS}}]
};

/**
 * Validation Results Collector
 */
class ValidationResults {
  constructor() {
    this.results = [];
    this.summary = {
      total_files: 0,
      valid_files: 0,
      invalid_files: 0,
      error_files: 0,
      warnings: 0,
      errors: 0
    };
  }

  addResult(result) {
    this.results.push(result);
    this.summary.total_files++;

    if (result.status === 'valid') {
      this.summary.valid_files++;
    } else if (result.status === 'invalid') {
      this.summary.invalid_files++;
    } else if (result.status === 'error') {
      this.summary.error_files++;
    }

    this.summary.warnings += result.warnings?.length || 0;
    this.summary.errors += result.errors?.length || 0;
  }

  isValid() {
    return this.summary.invalid_files === 0 && this.summary.error_files === 0;
  }

  generateReport(format = 'text') {
    if (format === 'json') {
      return JSON.stringify({
        validation: VALIDATION_CONFIG,
        timestamp: new Date().toISOString(),
        summary: this.summary,
        results: this.results
      }, null, 2);
    }

    return this.generateTextReport();
  }

  generateTextReport() {
    let report = [];

    report.push(`üîç ${VALIDATION_CONFIG.name} Validation Report`);
    report.push(`   Generated: ${new Date().toISOString()}`);
    report.push(`   Version: ${VALIDATION_CONFIG.version}`);
    report.push('');

    // Summary
    report.push('üìä Summary:');
    report.push(`   Total files: ${this.summary.total_files}`);
    report.push(`   ‚úÖ Valid: ${this.summary.valid_files}`);
    report.push(`   ‚ùå Invalid: ${this.summary.invalid_files}`);
    report.push(`   üö® Errors: ${this.summary.error_files}`);
    report.push(`   ‚ö†Ô∏è  Warnings: ${this.summary.warnings}`);
    report.push('');

    // Results by status
    const invalidFiles = this.results.filter(r => r.status === 'invalid');
    const errorFiles = this.results.filter(r => r.status === 'error');

    if (errorFiles.length > 0) {
      report.push('üö® Files with Errors:');
      errorFiles.forEach(result => {
        report.push(`   ${result.file}`);
        if (result.errors) {
          result.errors.forEach(error => {
            report.push(`     ‚Ä¢ ${error}`);
          });
        }
      });
      report.push('');
    }

    if (invalidFiles.length > 0) {
      report.push('‚ùå Invalid Files:');
      invalidFiles.forEach(result => {
        report.push(`   ${result.file}`);
        if (result.errors) {
          result.errors.forEach(error => {
            report.push(`     ‚Ä¢ ${error.instancePath || 'root'}: ${error.message || error}`);
          });
        }
      });
      report.push('');
    }

    // Overall status
    if (this.isValid()) {
      report.push('‚úÖ All validations passed!');
    } else {
      report.push('‚ùå Validation failed. Fix errors above.');
      report.push('üí° Run with --fix to attempt automatic fixes.');
    }

    return report.join('\n');
  }
}

/**
 * Load and compile validation schema
 */
async function loadSchema() {
  try {
    const schemaContent = await fs.readFile(VALIDATION_CONFIG.schema_path, 'utf-8');
    const schema = JSON.parse(schemaContent);

    const ajv = new Ajv({ allErrors: true });
    addFormats(ajv);

    return { schema, ajv };
  } catch (error) {
    throw new Error(`Failed to load schema from ${VALIDATION_CONFIG.schema_path}: ${error.message}`);
  }
}

/**
 * Find files to validate
 */
async function findFilesToValidate() {
  const files = [];

  for (const dir of VALIDATION_CONFIG.target_directories) {
    try {
      const entries = await fs.readdir(dir, { withFileTypes: true });

      for (const entry of entries) {
        if (entry.isFile()) {
          const filePath = path.join(dir, entry.name);

          // Check if file matches patterns
          const matches = VALIDATION_CONFIG.file_patterns.some(pattern => {
            const regex = new RegExp(pattern);
            return regex.test(entry.name);
          });

          if (matches) {
            files.push({
              path: filePath,
              name: entry.name,
              directory: dir,
              type: detectFileType(filePath)
            });
          }
        }
      }
    } catch (error) {
      if (error.code !== 'ENOENT') {
        console.warn(`Warning: Could not read directory ${dir}: ${error.message}`);
      }
    }
  }

  return files;
}

/**
 * Detect file type for validation
 */
function detectFileType(filePath) {
  if (filePath.includes('/commands/')) return 'command';
  if (filePath.includes('/agents/')) return 'agent';
  if (filePath.includes('/workflows/')) return 'workflow';
  if (filePath.includes('/hooks/')) return 'hook';
  if (filePath.includes('/knowledge/')) return 'knowledge';
  if (filePath.includes('/settings')) return 'settings';

  if (filePath.endsWith('.md')) return 'markdown';
  if (filePath.endsWith('.json')) return 'json';

  return 'unknown';
}

/**
 * Extract metadata from file
 */
async function extractMetadata(filePath, fileType) {
  const content = await fs.readFile(filePath, 'utf-8');

  if (fileType === 'json' || filePath.endsWith('.json')) {
    try {
      const data = JSON.parse(content);
      return data._metadata || data.metadata || data;
    } catch (error) {
      throw new Error(`Invalid JSON: ${error.message}`);
    }
  }

  // Extract YAML frontmatter from markdown files
  const frontmatterMatch = content.match(/^---\n(.*?)\n---/s);
  if (!frontmatterMatch) {
    throw new Error('No YAML frontmatter found');
  }

  try {
    return yaml.load(frontmatterMatch[1]);
  } catch (error) {
    throw new Error(`Invalid YAML frontmatter: ${error.message}`);
  }
}

/**
 * Validate single file
 */
async function validateFile(file, schema, ajv, options = {}) {
  const result = {
    file: path.relative(process.cwd(), file.path),
    type: file.type,
    status: 'unknown',
    errors: [],
    warnings: [],
    metadata: null
  };

  try {
    // Extract metadata
    result.metadata = await extractMetadata(file.path, file.type);

    // Determine schema to use
    let componentSchema;
    if (file.type === 'command' && schema.definitions?.command) {
      componentSchema = schema.definitions.command;
    } else if (file.type === 'agent' && schema.definitions?.agent) {
      componentSchema = schema.definitions.agent;
    } else if (file.type === 'workflow' && schema.definitions?.workflow) {
      componentSchema = schema.definitions.workflow;
    } else {
      componentSchema = schema.definitions?.metadata || schema;
    }

    // Validate against schema
    const validate = ajv.compile(componentSchema);
    const valid = validate(result.metadata);

    if (valid) {
      result.status = 'valid';

      // Additional validation checks
      const additionalChecks = await performAdditionalValidation(file, result.metadata, options);
      result.warnings.push(...additionalChecks.warnings);

      if (additionalChecks.errors.length > 0) {
        result.status = 'invalid';
        result.errors.push(...additionalChecks.errors);
      }

    } else {
      result.status = 'invalid';
      result.errors = validate.errors || [];
    }

  } catch (error) {
    result.status = 'error';
    result.errors = [error.message];
  }

  return result;
}

/**
 * Additional validation beyond schema
 */
async function performAdditionalValidation(file, metadata, options) {
  const checks = { errors: [], warnings: [] };

  // {{CUSTOM_VALIDATION_LOGIC}}

  // Check governance gate validity
  if (metadata.governance_gate && !['A', 'B', 'C'].includes(metadata.governance_gate)) {
    checks.errors.push('Invalid governance_gate: must be A, B, or C');
  }

  // Check version format
  if (metadata.version && !/^\d+\.\d+\.\d+$/.test(metadata.version)) {
    checks.warnings.push('Version should follow semantic versioning (x.y.z)');
  }

  // Check required author
  if (!metadata.created_by || metadata.created_by.trim() === '') {
    checks.errors.push('created_by is required and cannot be empty');
  }

  // Check date format
  if (metadata.created_date && !/^\d{4}-\d{2}-\d{2}$/.test(metadata.created_date)) {
    checks.warnings.push('created_date should be in YYYY-MM-DD format');
  }

  // {{ADDITIONAL_VALIDATION_RULES}}

  return checks;
}

/**
 * Attempt to fix validation errors automatically
 */
async function attemptAutoFix(file, result, options = {}) {
  if (result.status === 'valid') {
    return { fixed: false, message: 'File is already valid' };
  }

  const fixes = [];

  // {{AUTO_FIX_LOGIC}}

  // Example fixes
  if (result.errors.some(e => e.message?.includes('required property'))) {
    fixes.push('Add missing required metadata properties');
  }

  if (result.warnings.some(w => w.includes('version'))) {
    fixes.push('Update version to semantic versioning format');
  }

  if (options.dryRun) {
    return { 
      fixed: false, 
      message: `Would apply ${fixes.length} fixes: ${fixes.join(', ')}` 
    };
  }

  // Apply fixes here
  // ... implementation depends on specific validation type

  return { fixed: fixes.length > 0, message: `Applied ${fixes.length} fixes` };
}

/**
 * Main validation execution
 */
async function runValidation(options = {}) {
  const { fix = false, strict = false, format = 'text' } = options;

  console.log(`üîç Running ${VALIDATION_CONFIG.name} Validation`);
  console.log(`   Version: ${VALIDATION_CONFIG.version}`);
  console.log(`   Mode: ${strict ? 'STRICT' : 'NORMAL'}${fix ? ' + AUTO-FIX' : ''}`);

  try {
    // Load schema
    const { schema, ajv } = await loadSchema();

    // Find files to validate
    const files = await findFilesToValidate();

    if (files.length === 0) {
      console.log('üìù No files found to validate');
      return;
    }

    console.log(`üìä Found ${files.length} files to validate\n`);

    // Validate files
    const results = new ValidationResults();

    for (const file of files) {
      const result = await validateFile(file, schema, ajv, { strict });
      results.addResult(result);

      // Show progress
      const status = result.status === 'valid' ? '‚úÖ' : 
                    result.status === 'invalid' ? '‚ùå' : 'üö®';
      console.log(`${status} ${result.file}`);

      // Auto-fix if requested
      if (fix && result.status !== 'valid') {
        const fixResult = await attemptAutoFix(file, result, { dryRun: false });
        if (fixResult.fixed) {
          console.log(`   üîß ${fixResult.message}`);
        }
      }
    }

    // Generate and show report
    console.log('\n' + results.generateReport(format));

    // Exit with error code if validation failed
    if (!results.isValid()) {
      process.exit(1);
    }

  } catch (error) {
    console.error(`‚ùå Validation failed: ${error.message}`);
    process.exit(1);
  }
}

/**
 * CLI Interface
 */
function showUsage() {
  console.log(`Usage: node ${path.basename(process.argv[1])} [options]`);
  console.log(`\nOptions:`);
  console.log(`  --fix              Attempt to automatically fix validation errors`);
  console.log(`  --strict           Enable strict validation mode`);
  console.log(`  --format <format>  Output format: text (default) | json`);
  console.log(`  --help             Show this help message`);
  console.log(`\nExample:`);
  console.log(`  node ${path.basename(process.argv[1])}`);
  console.log(`  node ${path.basename(process.argv[1])} --fix --strict`);
  console.log(`  node ${path.basename(process.argv[1])} --format json`);
}

// Parse CLI arguments
const args = process.argv.slice(2);
const options = {
  fix: args.includes('--fix'),
  strict: args.includes('--strict'),
  format: args.includes('--format') ? args[args.indexOf('--format') + 1] : 'text',
  help: args.includes('--help')
};

if (options.help) {
  showUsage();
  process.exit(0);
}

runValidation(options);

