#!/usr/bin/env node

/**
 * Simple .claude/ Components Validation
 * No external dependencies version
 */

import fs from 'fs/promises';
import path from 'path';

const REQUIRED_METADATA_FIELDS = [
  'version', 'created_by', 'created_date', 'modified_date', 
  'type', 'status', 'platform', 'governance_gate'
];

const VALID_VALUES = {
  type: ['command', 'agent', 'workflow', 'hook', 'knowledge', 'settings'],
  status: ['active', 'deprecated', 'experimental'],
  platform: ['windows', 'linux', 'cross-platform'],
  governance_gate: ['A', 'B', 'C']
};

async function parseYAMLFrontmatter(content) {
  const match = content.match(/^---\n(.*?)\n---/s);
  if (!match) {
    throw new Error('No YAML frontmatter found');
  }

  const yamlContent = match[1];
  const metadata = {};

  // Simple YAML parsing (basic key: value pairs)
  const lines = yamlContent.split('\n');
  for (const line of lines) {
    const trimmed = line.trim();
    if (trimmed && !trimmed.startsWith('#')) {
      const colonIndex = trimmed.indexOf(':');
      if (colonIndex > 0) {
        const key = trimmed.substring(0, colonIndex).trim();
        let value = trimmed.substring(colonIndex + 1).trim();

        // Remove quotes and comments
        value = value.replace(/^["']|["']$/g, '');
        value = value.split('#')[0].trim();

        if (value) {
          metadata[key] = value;
        }
      }
    }
  }

  return metadata;
}

async function validateFile(filePath) {
  const result = {
    file: path.relative(process.cwd(), filePath),
    status: 'unknown',
    errors: [],
    warnings: [],
    metadata: null
  };

  try {
    const content = await fs.readFile(filePath, 'utf-8');
    result.metadata = await parseYAMLFrontmatter(content);

    // Check required fields
    for (const field of REQUIRED_METADATA_FIELDS) {
      if (!result.metadata[field]) {
        result.errors.push(`Missing required field: ${field}`);
      }
    }

    // Validate field values
    for (const [field, validValues] of Object.entries(VALID_VALUES)) {
      if (result.metadata[field] && !validValues.includes(result.metadata[field])) {
        result.errors.push(`Invalid ${field}: ${result.metadata[field]} (must be one of: ${validValues.join(', ')})`);
      }
    }

    // Check version format
    if (result.metadata.version && !/^\d+\.\d+\.\d+$/.test(result.metadata.version)) {
      result.warnings.push('Version should follow semantic versioning (x.y.z)');
    }

    // Check date format
    if (result.metadata.created_date && !/^\d{4}-\d{2}-\d{2}$/.test(result.metadata.created_date)) {
      result.warnings.push('created_date should be in YYYY-MM-DD format');
    }

    result.status = result.errors.length > 0 ? 'invalid' : 'valid';

  } catch (error) {
    result.status = 'error';
    result.errors = [error.message];
  }

  return result;
}

async function runValidation() {
  const directories = ['.claude/commands', '.claude/agents', '.claude/workflows'];

  console.log('üîç Validating .claude/ Template System');
  console.log('   Simple validation without external dependencies\n');

  let totalFiles = 0;
  let validFiles = 0;
  let invalidFiles = 0;
  let errorFiles = 0;
  const allResults = [];

  for (const dir of directories) {
    try {
      const files = await fs.readdir(dir);
      const mdFiles = files.filter(f => f.endsWith('.md') && f !== 'README.md');

      console.log(`üìÅ ${dir}/ (${mdFiles.length} files)`);

      for (const file of mdFiles) {
        totalFiles++;
        const filePath = path.join(dir, file);
        const result = await validateFile(filePath);
        allResults.push(result);

        const status = result.status === 'valid' ? '‚úÖ' : 
                      result.status === 'invalid' ? '‚ùå' : 'üö®';
        console.log(`   ${status} ${file}`);

        if (result.status === 'valid') validFiles++;
        else if (result.status === 'invalid') invalidFiles++;
        else errorFiles++;
      }
      console.log('');

    } catch (error) {
      if (error.code !== 'ENOENT') {
        console.log(`   ‚ö†Ô∏è  Error reading ${dir}: ${error.message}\n`);
      }
    }
  }

  // Summary
  console.log('üìä Validation Summary:');
  console.log(`   Total files: ${totalFiles}`);
  console.log(`   ‚úÖ Valid: ${validFiles}`);
  console.log(`   ‚ùå Invalid: ${invalidFiles}`);
  console.log(`   üö® Errors: ${errorFiles}`);
  console.log('');

  // Show details for invalid/error files
  const problemFiles = allResults.filter(r => r.status !== 'valid');
  if (problemFiles.length > 0) {
    console.log('üîç Issues Found:');
    problemFiles.forEach(result => {
      console.log(`\n${result.file}:`);
      result.errors.forEach(error => {
        console.log(`   ‚ùå ${error}`);
      });
      result.warnings.forEach(warning => {
        console.log(`   ‚ö†Ô∏è  ${warning}`);
      });
    });
    console.log('');
  }

  if (invalidFiles === 0 && errorFiles === 0) {
    console.log('‚úÖ All .claude/ components are valid!');
    console.log('üéØ Template system is working correctly.');
  } else {
    console.log('‚ùå Some components failed validation.');
    console.log('üí° Review and fix the issues above.');
    process.exit(1);
  }
}

runValidation().catch(console.error);

