#!/usr/bin/env node

/**
 * Generate Claude Code Command from Template
 * Usage: node generate-command.js <command-name>
 */

import fs from 'fs/promises';
import path from 'path';

const TEMPLATE_PATH = '.claude/templates/command.md.tpl';
const OUTPUT_DIR = '.claude/commands';

async function generateCommand(commandName, options = {}) {
  const {
    author = 'Omar El Mountassir',
    status = 'active',
    platform = 'cross-platform',
    gate = 'C',
    briefDescription = 'Brief description here',
    detailedDescription = 'Detailed description of what this command does.',
    npmScripts = [],
    permissions = [],
    dependencies = []
  } = options;

  const date = new Date().toISOString().split('T')[0];
  const version = '1.0.0';

  try {
    // Read template
    const template = await fs.readFile(TEMPLATE_PATH, 'utf-8');

    // Replace placeholders
    const content = template
      .replace(/\{\{VERSION\}\}/g, version)
      .replace(/\{\{AUTHOR\}\}/g, author)
      .replace(/\{\{DATE\}\}/g, date)
      .replace(/\{\{STATUS\}\}/g, status)
      .replace(/\{\{PLATFORM\}\}/g, platform)
      .replace(/\{\{GATE\}\}/g, gate)
      .replace(/\{\{COMMAND_NAME\}\}/g, commandName)
      .replace(/\{\{BRIEF_DESCRIPTION\}\}/g, briefDescription)
      .replace(/\{\{DETAILED_DESCRIPTION\}\}/g, detailedDescription)
      .replace(/\{\{DEPENDENCIES\}\}/g, JSON.stringify(dependencies))
      .replace(/\{\{NPM_SCRIPTS\}\}/g, JSON.stringify(npmScripts))
      .replace(/\{\{PERMISSIONS\}\}/g, JSON.stringify(permissions))
      // Add placeholders for actions, parameters, etc.
      .replace(/\{\{ACTION_1_NAME\}\}/g, 'Primary Action')
      .replace(/\{\{ACTION_1_DESCRIPTION\}\}/g, 'Description of primary action')
      .replace(/\{\{PARAMETERS\}\}/g, 'options')
      .replace(/\{\{DEFAULT_BEHAVIOR\}\}/g, 'Default behavior description')
      .replace(/\{\{GOVERNANCE_NOTES\}\}/g, `Approved at Gate ${gate} per mission charter requirements.`);

    // Ensure output directory exists
    await fs.mkdir(OUTPUT_DIR, { recursive: true });

    // Write generated command
    const outputPath = path.join(OUTPUT_DIR, `${commandName}.md`);
    await fs.writeFile(outputPath, content);

    console.log(`‚úÖ Generated command: ${outputPath}`);
    console.log(`üìù Edit the file to complete the template placeholders`);
    console.log(`üîç Validate with: node .claude/scripts/validate-schema.js ${outputPath}`);

  } catch (error) {
    console.error(`‚ùå Error generating command: ${error.message}`);
    process.exit(1);
  }
}

// CLI usage
const commandName = process.argv[2];
if (!commandName) {
  console.error('Usage: node generate-command.js <command-name>');
  console.error('Example: node generate-command.js analyze');
  process.exit(1);
}

generateCommand(commandName);

