#!/usr/bin/env node

/**
 * Migrate Existing .claude/ Components to Template System
 * Usage: node migrate-claude-components.js [--dry-run] [--force]
 */

import fs from 'fs/promises';
import path from 'path';

import yaml from 'js-yaml';

const COMPONENTS_TO_MIGRATE = {
  commands: {
    sourceDir: '.claude/commands',
    template: '.claude/templates/command.md.tpl',
    filePattern: /\.md$/,
    excludeFiles: ['README.md']
  },
  agents: {
    sourceDir: '.claude/agents',
    template: '.claude/templates/agent.md.tpl',
    filePattern: /\.md$/,
    excludeFiles: []
  },
  workflows: {
    sourceDir: '.claude/workflows',
    template: '.claude/templates/workflow.md.tpl',
    filePattern: /\.md$/,
    excludeFiles: ['index.md']
  }
};

/**
 * Migrate command files to new template format
 */
async function migrateCommand(file, template) {
  const content = file.content;

  // Extract existing information
  const titleMatch = content.match(/^# (\/\w+) - (.+)$/m);
  const commandName = titleMatch ? titleMatch[1].replace('/', '') : file.name.replace('.md', '');
  const briefDescription = titleMatch ? titleMatch[2] : 'Command description';

  // Extract usage pattern
  const usageMatch = content.match(/\*\*Usage:\*\* `([^`]+)`/);
  const _usage = usageMatch ? usageMatch[1] : `/${commandName} [options]`;

  // Extract actions
  const actionsMatch = content.match(/\*\*Actions:\*\*(.*?)(?=\*\*|$)/s);
  let actions = [];
  if (actionsMatch) {
    const actionLines = actionsMatch[1].split('\n').filter(line => line.trim().startsWith('1.') || line.trim().startsWith('2.') || line.trim().startsWith('3.'));
    actions = actionLines.map(line => {
      const match = line.match(/\d+\.\s*\*\*([^:]+):\*\*\s*(.+)/);
      return match ? { name: match[1], description: match[2] } : null;
    }).filter(Boolean);
  }

  // Generate metadata
  const metadata = {
    version: '1.0.0',
    created_by: 'Omar El Mountassir',
    created_date: new Date().toISOString().split('T')[0],
    modified_date: new Date().toISOString().split('T')[0],
    type: 'command',
    status: 'active',
    dependencies: [],
    platform: 'cross-platform',
    governance_gate: 'C',
    npm_scripts_required: extractNpmScripts(content),
    permissions_required: extractPermissions(content)
  };

  // Replace template placeholders
  let migratedContent = template
    .replace(/\{\{VERSION\}\}/g, metadata.version)
    .replace(/\{\{AUTHOR\}\}/g, metadata.created_by)
    .replace(/\{\{DATE\}\}/g, metadata.created_date)
    .replace(/\{\{STATUS\}\}/g, metadata.status)
    .replace(/\{\{DEPENDENCIES\}\}/g, JSON.stringify(metadata.dependencies))
    .replace(/\{\{PLATFORM\}\}/g, metadata.platform)
    .replace(/\{\{GATE\}\}/g, metadata.governance_gate)
    .replace(/\{\{COMMAND_NAME\}\}/g, commandName)
    .replace(/\{\{BRIEF_DESCRIPTION\}\}/g, briefDescription)
    .replace(/\{\{DETAILED_DESCRIPTION\}\}/g, extractDetailedDescription(content))
    .replace(/\{\{NPM_SCRIPTS\}\}/g, JSON.stringify(metadata.npm_scripts_required))
    .replace(/\{\{PERMISSIONS\}\}/g, JSON.stringify(metadata.permissions_required))
    .replace(/\{\{PARAMETERS\}\}/g, 'options')
    .replace(/\{\{DEFAULT_BEHAVIOR\}\}/g, 'Default command behavior')
    .replace(/\{\{GOVERNANCE_NOTES\}\}/g, `Migrated from existing command. Gate ${metadata.governance_gate} approval pending review.`);

  // Replace action placeholders
  if (actions.length > 0) {
    migratedContent = migratedContent.replace(/\{\{ACTION_1_NAME\}\}/g, actions[0]?.name || 'Primary Action');
    migratedContent = migratedContent.replace(/\{\{ACTION_1_DESCRIPTION\}\}/g, actions[0]?.description || 'Primary action description');
  }

  return migratedContent;
}

/**
 * Migrate agent files to new template format
 */
async function migrateAgent(file, template) {
  const content = file.content;

  // Extract existing frontmatter
  const frontmatterMatch = content.match(/^---\n(.*?)\n---/s);
  let existingMetadata = {};

  if (frontmatterMatch) {
    try {
      existingMetadata = yaml.load(frontmatterMatch[1]);
    } catch (_error) {
      console.warn(`Warning: Could not parse frontmatter in ${file.name}`);
    }
  }

  // Generate new metadata
  const metadata = {
    name: existingMetadata.name || file.name.replace('.md', ''),
    version: '1.0.0',
    created_by: 'Omar El Mountassir',
    created_date: new Date().toISOString().split('T')[0],
    modified_date: new Date().toISOString().split('T')[0],
    type: 'agent',
    status: 'active',
    specialization: existingMetadata.description || 'Agent specialization',
    tools: existingMetadata.tools || ['Read', 'Edit', 'Bash'],
    platform: 'cross-platform',
    governance_gate: 'C',
    triggers: ['manual']
  };

  // Extract agent instructions
  const instructionsMatch = content.match(/You are a (.+?) specializing in (.+?)\./);
  const agentRole = instructionsMatch ? instructionsMatch[1] : 'specialist';
  const specialization = instructionsMatch ? instructionsMatch[2] : metadata.specialization;

  // Replace template placeholders
  const migratedContent = template
    .replace(/\{\{AGENT_NAME\}\}/g, metadata.name)
    .replace(/\{\{VERSION\}\}/g, metadata.version)
    .replace(/\{\{AUTHOR\}\}/g, metadata.created_by)
    .replace(/\{\{DATE\}\}/g, metadata.created_date)
    .replace(/\{\{STATUS\}\}/g, metadata.status)
    .replace(/\{\{SPECIALIZATION\}\}/g, specialization)
    .replace(/\{\{TOOLS\}\}/g, JSON.stringify(metadata.tools))
    .replace(/\{\{PLATFORM\}\}/g, metadata.platform)
    .replace(/\{\{GATE\}\}/g, metadata.governance_gate)
    .replace(/\{\{TRIGGERS\}\}/g, JSON.stringify(metadata.triggers))
    .replace(/\{\{AGENT_DISPLAY_NAME\}\}/g, metadata.name.charAt(0).toUpperCase() + metadata.name.slice(1))
    .replace(/\{\{AGENT_DESCRIPTION\}\}/g, existingMetadata.description || 'Agent description')
    .replace(/\{\{AGENT_ROLE\}\}/g, agentRole)
    .replace(/\{\{SPECIALIZATION_DETAILS\}\}/g, specialization)
    .replace(/\{\{USAGE_TRIGGERS\}\}/g, 'Use this agent when specialized expertise is needed')
    .replace(/\{\{GOVERNANCE_NOTES\}\}/g, `Migrated from existing agent. Gate ${metadata.governance_gate} approval pending review.`);

  return migratedContent;
}

/**
 * Extract npm scripts mentioned in content
 */
function extractNpmScripts(content) {
  const scripts = [];
  const npmMatches = content.matchAll(/npm run ([a-zA-Z:-]+)/g);
  for (const match of npmMatches) {
    if (!scripts.includes(match[1])) {
      scripts.push(match[1]);
    }
  }
  return scripts;
}

/**
 * Extract permissions mentioned in content
 */
function extractPermissions(content) {
  const permissions = [];
  if (content.includes('npm run review') || content.includes('npm test')) {
    permissions.push('Bash(npm run review)', 'Bash(npm test)');
  }
  if (content.includes('git')) {
    permissions.push('Bash(git status)', 'Bash(git diff)');
  }
  return [...new Set(permissions)];
}

/**
 * Extract detailed description from content
 */
function extractDetailedDescription(content) {
  // Look for content after title and before **Usage:**
  const match = content.match(/^# \/\w+ - .+\n\n(.*?)(?=\*\*Usage:\*\*)/s);
  return match ? match[1].trim() : 'Detailed description of command functionality.';
}

/**
 * Main migration execution
 */
async function runMigration(options = {}) {
  const { dryRun = false, force: _force = false } = options;

  console.log('üöÄ Migrating .claude/ components to template system');
  console.log(`   Mode: ${dryRun ? 'DRY RUN' : 'LIVE'}`);

  let totalFiles = 0;
  let migratedFiles = 0;
  const backupDir = `.claude/backup-${new Date().toISOString().replace(/[:.]/g, '-')}`;

  try {
    for (const [componentType, config] of Object.entries(COMPONENTS_TO_MIGRATE)) {
      console.log(`\nüìÅ Processing ${componentType}...`);

      try {
        const files = await fs.readdir(config.sourceDir);
        const mdFiles = files.filter(f => 
          config.filePattern.test(f) && 
          !config.excludeFiles.includes(f)
        );

        if (mdFiles.length === 0) {
          console.log(`   No files found to migrate`);
          continue;
        }

        // Load template
        const template = await fs.readFile(config.template, 'utf-8');

        for (const fileName of mdFiles) {
          totalFiles++;
          const filePath = path.join(config.sourceDir, fileName);
          const content = await fs.readFile(filePath, 'utf-8');

          const file = {
            name: fileName,
            path: filePath,
            content
          };

          console.log(`   üîÑ ${fileName}`);

          try {
            let migratedContent;

            if (componentType === 'commands') {
              migratedContent = await migrateCommand(file, template);
            } else if (componentType === 'agents') {
              migratedContent = await migrateAgent(file, template);
            } else {
              // Default migration for other types
              migratedContent = content; // Keep original for now
            }

            if (dryRun) {
              console.log(`      [DRY RUN] Would migrate to template format`);
            } else {
              // Create backup first
              await fs.mkdir(backupDir, { recursive: true });
              const backupPath = path.join(backupDir, fileName);
              await fs.copyFile(filePath, backupPath);

              // Write migrated content
              await fs.writeFile(filePath, migratedContent);
              console.log(`      ‚úÖ Migrated successfully`);
            }

            migratedFiles++;

          } catch (error) {
            console.log(`      ‚ùå Migration failed: ${error.message}`);
          }
        }

      } catch (error) {
        if (error.code !== 'ENOENT') {
          console.log(`   ‚ö†Ô∏è  Could not process ${componentType}: ${error.message}`);
        }
      }
    }

    // Summary
    console.log(`\nüìä Migration Summary:`);
    console.log(`   Total files: ${totalFiles}`);
    console.log(`   Migrated: ${migratedFiles}`);

    if (!dryRun && migratedFiles > 0) {
      console.log(`   üì¶ Backup: ${backupDir}`);
      console.log(`\n‚úÖ Migration completed!`);
      console.log(`üí° Next steps:`);
      console.log(`   1. Review migrated files for accuracy`);
      console.log(`   2. Run: npm run claude:validate`);
      console.log(`   3. Test functionality of migrated components`);
      console.log(`   4. Commit changes with proper governance approval`);
    }

  } catch (error) {
    console.error(`‚ùå Migration failed: ${error.message}`);
    process.exit(1);
  }
}

// CLI parsing
const args = process.argv.slice(2);
const options = {
  dryRun: args.includes('--dry-run'),
  force: args.includes('--force')
};

if (args.includes('--help')) {
  console.log('Usage: node migrate-claude-components.js [--dry-run] [--force]');
  console.log('  --dry-run    Preview changes without modifying files');
  console.log('  --force      Proceed with migration');
  process.exit(0);
}

runMigration(options);

