#!/usr/bin/env node
import crypto from 'crypto';
import fs from 'fs';
import path from 'path';

import yaml from 'js-yaml';

const root = process.cwd();
const covRoot = path.join(root, 'control-plane', 'covenants');
const fragDir = path.join(covRoot, 'fragments');

function sha256(str) {
  return crypto.createHash('sha256').update(str).digest('hex');
}

function readAllFragments(dir) {
  const entries = fs.readdirSync(dir, { withFileTypes: true });
  let frags = [];
  for (const e of entries) {
    const p = path.join(dir, e.name);
    if (e.isDirectory()) frags = frags.concat(readAllFragments(p));
    else if (e.name.endsWith('.md')) {
      const content = fs.readFileSync(p, 'utf8');
      frags.push({ file: p, content });
    }
  }
  return frags;
}

function classify(frags) {
  const core = [];
  const overlays = { agent: {}, audience: {}, package: {} };
  for (const f of frags) {
    if (f.content.startsWith('<!-- fragment:key=')) core.push(f);
    else if (f.content.startsWith('<!-- overlay:agent=')) {
      const key = f.content.split('=')[1].split(' ')[0].trim().replace('-->', '');
      overlays.agent[key] = (overlays.agent[key] || []).concat(f);
    } else if (f.content.startsWith('<!-- overlay:audience=')) {
      const key = f.content.split('=')[1].split(' ')[0].trim().replace('-->', '');
      overlays.audience[key] = (overlays.audience[key] || []).concat(f);
    } else if (f.content.startsWith('<!-- overlay:package=')) {
      const key = f.content.split('=')[1].split(' ')[0].trim().replace('-->', '');
      overlays.package[key] = (overlays.package[key] || []).concat(f);
    }
  }
  return { core, overlays };
}

function buildOutput({ agent, audience, pkg }, classified) {
  const lines = [];
  lines.push('<!-- GENERATED: do not edit directly. Use fragments. -->');
  lines.push(`# Unified Covenant (${agent || audience || 'generic'})`);
  const sortedCore = [...classified.core].sort((a, b) => a.file.localeCompare(b.file));
  for (const f of sortedCore) {
    lines.push(f.content.trim());
  }
  if (agent && classified.overlays.agent[agent]) {
    lines.push('\n<!-- Agent Overlay -->');
    classified.overlays.agent[agent].forEach((o) => lines.push(o.content.trim()));
  }
  if (pkg && classified.overlays.package[pkg]) {
    lines.push('\n<!-- Package Overlay -->');
    classified.overlays.package[pkg].forEach((o) => lines.push(o.content.trim()));
  }
  if (audience && classified.overlays.audience[audience]) {
    lines.push('\n<!-- Audience Overlay -->');
    classified.overlays.audience[audience].forEach((o) => lines.push(o.content.trim()));
  }
  return lines.join('\n\n');
}

function writeIfChanged(file, content) {
  if (fs.existsSync(file)) {
    const prev = fs.readFileSync(file, 'utf8');
    if (prev === content) return false;
  }
  fs.mkdirSync(path.dirname(file), { recursive: true });
  fs.writeFileSync(file, content);
  return true;
}

function main() {
  const frags = readAllFragments(fragDir);
  const classified = classify(frags);
  // Build structured representation (meta + sections) for schema validation & machine use
  const sections = [];
  const coreSorted = [...classified.core].sort((a, b) => a.file.localeCompare(b.file));
  for (const f of coreSorted) {
    const keyMatch = f.content.match(/fragment:key=([^\s>]+)/);
    const titleMatch = f.content.match(/\*\*(.+?)\*\*/); // first bold segment
    sections.push({
      key: keyMatch ? keyMatch[1].trim() : path.basename(f.file),
      title: titleMatch ? titleMatch[1].replace(/\.$/, '') : path.basename(f.file),
      content: f.content.trim(),
    });
  }
  const overlaysCollected = [];
  for (const [agent, arr] of Object.entries(classified.overlays.agent)) {
    overlaysCollected.push({ scope: 'agent', key: agent, fragments: arr });
  }
  for (const [aud, arr] of Object.entries(classified.overlays.audience)) {
    overlaysCollected.push({ scope: 'audience', key: aud, fragments: arr });
  }
  for (const [pkg, arr] of Object.entries(classified.overlays.package)) {
    overlaysCollected.push({ scope: 'package', key: pkg, fragments: arr });
  }
  for (const ov of overlaysCollected) {
    for (const f of ov.fragments) {
      const titleMatch = f.content.match(/\*\*(.+?)\*\*/);
      sections.push({
        key: `${ov.scope}:${ov.key}`,
        title: titleMatch ? titleMatch[1].replace(/\.$/, '') : `${ov.scope}:${ov.key}`,
        content: f.content.trim(),
      });
    }
  }
  const pkgJsonPath = path.join(root, 'package.json');
  let version = '0.0.0';
  if (fs.existsSync(pkgJsonPath)) {
    try {
      version = JSON.parse(fs.readFileSync(pkgJsonPath, 'utf8')).version || version;
    } catch (e) {
      console.warn('[covenants] could not read version:', e.message);
    }
  }
  const covenantJson = { meta: { id: 'unified-covenant', version, generated_at: new Date().toISOString() }, sections };

  // Schema validation (minimal) using provided YAML schema
  const schemaPath = path.join(covRoot, 'schema', 'agent-covenant.schema.yaml');
  if (fs.existsSync(schemaPath)) {
    try {
      const schema = yaml.load(fs.readFileSync(schemaPath, 'utf8'));
      const errors = [];
      // Required top-level
      for (const req of schema.required || []) if (!(req in covenantJson)) errors.push(`Missing root field: ${req}`);
      if (typeof covenantJson.meta !== 'object') errors.push('meta must be object');
      else {
        const metaReq = schema.properties?.meta?.required || [];
        for (const mr of metaReq) if (!(mr in covenantJson.meta)) errors.push(`Missing meta field: ${mr}`);
      }
      if (!Array.isArray(covenantJson.sections)) errors.push('sections must be array');
      else if (!covenantJson.sections.length) errors.push('sections must not be empty');
      else {
        covenantJson.sections.forEach((s, i) => {
          ['key', 'title', 'content'].forEach((k) => {
            if (!s[k] || typeof s[k] !== 'string') errors.push(`sections[${i}].${k} missing/invalid`);
          });
        });
      }
      if (errors.length) {
        console.error('[covenants] schema validation failed:', errors.slice(0, 10));
        process.exit(1);
      }
    } catch (e) {
      console.warn('[covenants] schema validation skipped (parse error):', e.message);
    }
  }
  const outputs = [
    { file: path.join(root, '.github', 'copilot-instructions.md'), agent: 'copilot' },
    { file: path.join(root, 'CLAUDE.md'), agent: 'claude' },
    {
      file: path.join(root, 'control-plane', 'governance', 'copilot-covenant.mdx'),
      audience: 'human',
    },
  ];
  const manifest = [];
  for (const o of outputs) {
    const content = buildOutput({ agent: o.agent, audience: o.audience }, classified);
    const changed = writeIfChanged(o.file, content + '\n');
    manifest.push({ target: o.file, hash: sha256(content), changed });
  }
  // Write structured JSON artifact
  const jsonOut = path.join(covRoot, 'covenant.json');
  fs.writeFileSync(jsonOut, JSON.stringify(covenantJson, null, 2));
  manifest.push({ target: jsonOut, hash: sha256(JSON.stringify(covenantJson)), changed: true });
  fs.writeFileSync(
    path.join(covRoot, 'covenants.manifest.json'),
    JSON.stringify({ generated_at: new Date().toISOString(), outputs: manifest }, null, 2)
  );
  const baselinePath = path.join(covRoot, 'baseline-sources.json');
  if (!fs.existsSync(baselinePath)) {
    const humanCovenantPath = path.join(
      root,
      'control-plane',
      'governance',
      'copilot-covenant.mdx'
    );
    const sources = [];
    if (fs.existsSync(humanCovenantPath)) {
      const raw = fs.readFileSync(humanCovenantPath, 'utf8');
      const lines = raw.split(/\r?\n/).filter((l) => l.trim().length > 0);
      sources.push({ source: 'copilot-covenant.mdx', hash: sha256(raw), lines });
    }
    fs.writeFileSync(
      baselinePath,
      JSON.stringify({ generated_at: new Date().toISOString(), sources }, null, 2)
    );
  }
  console.log('Covenants generated:', manifest.map((m) => path.basename(m.target)).join(', '));
}

main();
